            switchTab(tabId);
        }
    });

    // 点击条目事件
    $panel.off('click.lorebookEntries').on('click.lorebookEntries', `.${LOREBOOK_ENTRY_CLASS}`, function(e) {
        // 如果点击的是开关，则不触发任何操作
        if ($(e.target).closest(`.${LOREBOOK_TOGGLE_SWITCH_CLASS}`).length > 0) {
            return;
        }

        // 确保UID为数字
        const entryUid = ensureNumericUID($(this).data('entry-uid'));
        const lorebookName = $(this).data('entry-lorebook');
        const isGlobal = $(this).closest('.lorebook-entries-container').attr('data-is-global') === 'true';

        console.log(`角色世界书: 点击条目，条目ID=${entryUid}, 世界书=${lorebookName}, 全局=${isGlobal}`);

        // 直接打开编辑器
        showEntryEditor(lorebookName, entryUid, isGlobal);
    });

    // 全局世界书标题点击事件
    $panel.off('click.lorebookTitleClick').on('click.lorebookTitleClick', '.lorebook-title-clickable', function(e) {
        // 阻止事件冒泡，避免与其他可能的点击事件冲突
        e.stopPropagation();

        // 如果点击来源是搜索框或其相关元素，不处理折叠/展开
        if ($(e.target).closest('.lorebook-search-container').length > 0 ||
            $(e.target).closest('.lorebook-search-input').length > 0 ||
            $(e.target).closest('.lorebook-search-clear').length > 0 ||
            $(e.target).closest('.lorebook-search-icon').length > 0) {
            return;
        }

        const lorebookName = $(this).attr('data-lorebook-name');
        const isExpanded = $(this).attr('data-expanded') === 'true';
        const isLoaded = $(this).attr('data-loaded') === 'true';
        const isGlobal = true; // 这个事件只用于全局世界书

        console.log(`角色世界书: 点击世界书标题 ${lorebookName}，当前状态: 展开=${isExpanded}, 已加载=${isLoaded}`);

