        const $entriesWrapper = $(`.lorebook-entries-wrapper[data-lorebook-name="${lorebookName}"]`);

        if (!$entriesWrapper.length) {
            console.error(`角色世界书: 找不到世界书 ${lorebookName} 的条目容器`);
            return;
        }

        // 获取展开/折叠指示图标
        const $expandIcon = $(this).find('.lorebook-expand-icon');

        if (isExpanded) {
            // 如果已展开，则折叠
            $entriesWrapper.slideUp(300);
            $(this).attr('data-expanded', 'false');
            $expandIcon.removeClass('fa-chevron-up').addClass('fa-chevron-down');
        } else {
            // 如果未展开，展开并按需加载
            if (!isLoaded) {
                // 如果未加载过，则加载条目
                loadLorebookEntries(lorebookName, $entriesWrapper, isGlobal).then(success => {
                    if (success) {
                        // 加载成功后展开
                        $entriesWrapper.slideDown(300);
                        $(this).attr('data-expanded', 'true');
                        $(this).attr('data-loaded', 'true');
                        $expandIcon.removeClass('fa-chevron-down').addClass('fa-chevron-up');
                    } else {
                        // 加载失败的处理
                        console.error(`角色世界书: 加载世界书 ${lorebookName} 条目失败`);
                        $entriesWrapper.html('<div class="no-entries-message">加载失败，请重试</div>').show();
                        $(this).attr('data-expanded', 'true');
                        $expandIcon.removeClass('fa-chevron-down').addClass('fa-chevron-up');
                    }
                });
            } else {
                // 如果已加载过，直接展开
                $entriesWrapper.slideDown(300);
                $(this).attr('data-expanded', 'true');
                $expandIcon.removeClass('fa-chevron-down').addClass('fa-chevron-up');
            }
        }
