                    // 保存当前滚动位置
                    const parentDoc = window.parent.document;
                    const $panel = $(`#${LOREBOOK_PANEL_ID}`, parentDoc);
                    if ($panel.is(':visible')) {
                        if (result.isGlobal) {
                            // 全局世界书条目
                            const $list = $panel.find(`#${GLOBAL_LOREBOOK_LIST_CONTAINER_ID}`);
