                $debugInfo.text(`保存失败: ${result.message}`).css('color', 'red').show();
                console.error(`保存失败: ${result.message}`);
                $saveBtn.text(originalText).prop('disabled', false);
            }
        }).catch(error => {
            $debugInfo.text(`保存出错: ${error.message || '未知错误'}`).css('color', 'red').show();
