            e.stopPropagation();
        });

        // 搜索框点击事件 - 阻止事件冒泡
        $panel.on('click', '.lorebook-search-container', function(e) {
            e.stopPropagation();
        });

        // 不再需要清除按钮的点击事件处理
        // $panel.on('click', '.lorebook-search-clear', function(e) {
        //     e.stopPropagation();
        // });

        // 搜索框输入事件
        $panel.on('input', '.lorebook-search-input', function() {
            const $input = $(this);
            const searchText = $input.val().trim();
            const lorebookName = $input.attr('data-lorebook-name');

            console.log(`角色世界书: 搜索事件触发: "${searchText}" 在世界书 "${lorebookName}"`);

            // 不再显示清除按钮
            // const $clearBtn = $input.siblings('.lorebook-search-clear');
            // if (searchText.length > 0) {
            //     $clearBtn.show();
            // } else {
            //     $clearBtn.hide();
            // }

            // 找到对应的条目容器
            const $entriesContainer = $panel.find(`.lorebook-entries-container[data-lorebook-name="${lorebookName}"]`);

            if ($entriesContainer.length) {
                // 搜索条目
                filterEntries($entriesContainer, searchText);
                console.log(`角色世界书: 找到条目容器，已过滤 ${$entriesContainer.find('.lorebook-entry-item').length} 个条目`);
            } else {
                console.error(`角色世界书: 找不到世界书 "${lorebookName}" 的条目容器`);
            }
        });

        // 不再需要清除按钮的点击事件
        // $panel.on('click', '.lorebook-search-clear', function(e) {
        //     e.preventDefault();
        //     e.stopPropagation();
        //     
        //     const $clearBtn = $(this);
        //     const $searchInput = $clearBtn.siblings('.lorebook-search-input');
        //     
        //     console.log('角色世界书: 点击了清除按钮');
        //     
        //     // 清空输入框并触发输入事件
        //     $searchInput.val('');
        //     $searchInput.trigger('input');
        //     
        //     // 隐藏清除按钮
        //     $clearBtn.hide();
        // });

        // 按ESC键清除搜索
        $panel.on('keydown', '.lorebook-search-input', function(e) {
            if (e.key === 'Escape') {
                const $input = $(this);
                // 如果输入框有内容，清空它
                if ($input.val().trim().length > 0) {
                    $input.val('');
                    $input.trigger('input');
                    console.log('角色世界书: 按ESC清除搜索');
                }
            }
        });

        console.log('角色世界书: 搜索事件绑定完成');
