    // 过滤条目函数 - 只搜索标题
    function filterEntries($container, searchText) {
        console.log(`角色世界书: 开始过滤条目，搜索文本: "${searchText}"`);

        if (!$container || !$container.length) {
            console.error('角色世界书: 找不到条目容器');
            return;
        }

        // 获取所有条目
        const $entries = $container.find(`.${LOREBOOK_ENTRY_CLASS}`);
        console.log(`角色世界书: 容器中共有 ${$entries.length} 个条目`);

        let visibleCount = 0;

        // 将搜索文本转为小写用于比较
        const lowerSearchText = searchText.toLowerCase().trim();

        // 如果没有搜索文本，显示所有条目
        if (!lowerSearchText) {
            $entries.show();
            console.log('角色世界书: 搜索文本为空，显示所有条目');

            // 移除之前的"无搜索结果"消息
            $container.find('.no-search-results').remove();
            return;
        }

        // 遍历所有条目，仅根据标题匹配搜索文本
        $entries.each(function() {
            const $entry = $(this);
            const entryTitle = $entry.find('.entry-item-title').val().toLowerCase();

            // 只搜索标题
            if (entryTitle.includes(lowerSearchText)) {
                $entry.show();
                visibleCount++;
            } else {
                $entry.hide();
            }
        });

        console.log(`角色世界书: 过滤后显示 ${visibleCount} 个条目`);

        // 如果没有匹配的条目，显示提示信息
        if (visibleCount === 0) {
            // 移除之前的消息（如果存在）
            $container.find('.no-search-results').remove();

            // 添加新的消息
            const $noResults = $('<div></div>')
                .addClass('no-search-results')
                .text(`找不到标题包含"${searchText}"的条目`);

            $container.append($noResults);
        } else {
            // 移除之前的"无搜索结果"消息
            $container.find('.no-search-results').remove();
        }
    }

    // 立即绑定一次搜索事件
    bindSearchEvents();

    console.log('角色世界书: 初始化完成。');

    // 添加事件监听，处理角色切换和应用准备就绪
    if (window.eventOn && window.tavern_events) {
        // 当角色切换时，如果面板已打开则刷新世界书面板
        window.eventOn(window.tavern_events.CHAT_CHANGED, function() {
            console.log('角色世界书: 检测到聊天/角色变更，检查是否需要刷新...');
            const $panel = $(`#${LOREBOOK_PANEL_ID}`, parentDoc);

            // 仅当面板已经打开时才刷新数据
            if ($panel.is(':visible')) {
                console.log('角色世界书: 面板已打开，刷新世界书数据...');
                const $list = $panel.find(`#${LOREBOOK_LIST_CONTAINER_ID}`);

                // 刷新面板
                updateBoundLorebooksList($list, true);

                // 面板刷新后重新绑定搜索事件
                setTimeout(bindSearchEvents, 500);
            } else {
                console.log('角色世界书: 面板未打开，不刷新数据');
                // 重置加载状态，以便下次打开时重新加载
                hasInitializedLorebooks = false;
            }
        });

        // 当应用准备就绪后初始化
        window.eventOn(window.tavern_events.APP_READY, function() {
            console.log('角色世界书: SillyTavern应用已准备就绪');
            // 只绑定搜索事件，不加载世界书数据
            bindSearchEvents();
        });
    } else {
        console.log('角色世界书: 事件系统不可用，无法监听角色切换事件');
    }

    // 批量删除选中的条目
    const deleteSelectedEntries = errorCatched(async (lorebookName, isGlobal = false) => {
        const parentDoc = window.parent.document;
        let $container;

        if (isGlobal) {
            // 对于全局世界书，找到特定世界书的条目容器
            $container = $(`.lorebook-entries-container[data-lorebook-name="${lorebookName}"][data-is-global="true"]`, parentDoc);
        } else {
            // 对于角色绑定世界书，找到特定世界书的条目容器
            $container = $(`.lorebook-entries-container[data-lorebook-name="${lorebookName}"]`, parentDoc);
        }

        if (!$container.length) {
            console.error(`角色世界书: 找不到世界书 ${lorebookName} 的条目容器`);
            return false;
        }

        // 获取所有选中的复选框
        const $selectedCheckboxes = $container.find(`.${LOREBOOK_ENTRY_CHECKBOX_CLASS}:checked`);

        if ($selectedCheckboxes.length === 0) {
            alert('请至少选择一个要删除的条目');
            return false;
        }

        // 收集所有要删除的条目信息
        const entriesToDelete = [];
        $selectedCheckboxes.each(function() {
            const entryUid = $(this).attr('data-entry-uid');
            const entryTitle = $(this).closest(`.${LOREBOOK_ENTRY_CLASS}`).find('.entry-item-title').val() || '未命名条目';
            entriesToDelete.push({
                uid: entryUid,
                title: entryTitle
            });
        });

        // 确认删除
        const confirmMessage = entriesToDelete.length === 1 
            ? `确定要删除条目"${entriesToDelete[0].title}"吗？` 
            : `确定要删除选中的 ${entriesToDelete.length} 个条目吗？`;

        if (!confirm(confirmMessage)) {
            return false;
        }

        // 批量删除
        try {
            if (TavernHelper && typeof TavernHelper.deleteLorebookEntries === 'function') {
                // 提取所有UID
                const uids = entriesToDelete.map(entry => ensureNumericUID(entry.uid));

                // 调用API删除条目
                await TavernHelper.deleteLorebookEntries(lorebookName, uids);

                // 刷新条目列表
                const $panel = $(`#${LOREBOOK_PANEL_ID}`, parentDoc);

                if (isGlobal) {
                    // 如果是全局世界书，只刷新对应世界书的条目区域
                    const $entriesWrapper = $(`.lorebook-entries-wrapper[data-lorebook-name="${lorebookName}"]`, parentDoc);
                    if ($entriesWrapper.length > 0) {
                        await loadLorebookEntries(lorebookName, $entriesWrapper, true);
                    } else {
                        // 如果找不到特定容器，刷新整个全局世界书列表
                        const $globalList = $panel.find(`#${GLOBAL_LOREBOOK_LIST_CONTAINER_ID}`);
                        updateGlobalLorebooksList($globalList, true);
                    }
                } else {
                    // 角色绑定世界书，刷新整个列表
                    const $list = $panel.find(`#${LOREBOOK_LIST_CONTAINER_ID}`);
                    updateBoundLorebooksList($list, true);
                }

                return true;
            } else {
                console.error('角色世界书: 删除条目API不可用');
                alert('删除条目API不可用，无法执行删除操作');
                return false;
            }
        } catch (error) {
            console.error(`角色世界书: 删除条目时出错`, error);
            alert(`删除条目时出错: ${error.message || '未知错误'}`);
            return false;
        }
    }, 'deleteSelectedEntries');

    // 将函数暴露到全局window对象
    window.deleteSelectedEntries = deleteSelectedEntries;

    // 添加全选/取消全选功能
    const toggleAllEntries = errorCatched((lorebookName, isGlobal, selectAll) => {
        const parentDoc = window.parent.document;
        let $container;

        if (isGlobal) {
            // 对于全局世界书，找到特定世界书的条目容器
            $container = $(`.lorebook-entries-container[data-lorebook-name="${lorebookName}"][data-is-global="true"]`, parentDoc);
        } else {
            // 对于角色绑定世界书，找到特定世界书的条目容器
            $container = $(`.lorebook-entries-container[data-lorebook-name="${lorebookName}"]`, parentDoc);
        }

        if (!$container.length) {
            console.error(`角色世界书: 找不到世界书 ${lorebookName} 的条目容器`);
            return false;
        }

        // 获取所有复选框并设置状态
        $container.find(`.${LOREBOOK_ENTRY_CHECKBOX_CLASS}`).prop('checked', selectAll);

        return true;
    }, 'toggleAllEntries');
});

// 上下移动条目函数
const moveEntryUpDown = errorCatched(async (lorebookName, entryUid, direction) => {
    const numericUid = ensureNumericUID(entryUid);
    const parentDoc = window.parent.document;

    try {
        const $item = $(`.${LOREBOOK_ENTRY_CLASS}[data-entry-uid="${numericUid}"]`, parentDoc);
        if (!$item.length) {
            console.error(`角色世界书: 找不到要移动的条目 ${numericUid}`);
            return false;
        }

        const $container = $item.closest('.lorebook-entries-container');
        const $items = $container.find(`.${LOREBOOK_ENTRY_CLASS}`);
        const itemIndex = $items.index($item);

        // 确定目标位置
        let targetIndex;
        if (direction === 'up') {
            if (itemIndex === 0) {
                return false;
            }
            targetIndex = itemIndex - 1;
        } else {
            if (itemIndex === $items.length - 1) {
                return false;
            }
            targetIndex = itemIndex + 1;
        }

        // 获取目标条目
        const $targetItem = $items.eq(targetIndex);

        // 交换两个条目的位置
        if (direction === 'up') {
            $item.insertBefore($targetItem);
        } else {
            $item.insertAfter($targetItem);
        }

        return true;
    } catch (error) {
        console.error(`角色世界书: 移动条目时出错`, error);
        return false;
    }
}, 'moveEntryUpDown');

/* --- 本地存储UI排序函数 --- */
// 保存特定世界书的UI排序到本地存储
function saveUISort(lorebookName, sortedIds) {
    try {
        let allSortData = {};
        const storedData = localStorage.getItem(LOREBOOK_UI_SORT_KEY);
        if (storedData) {
            try {
                allSortData = JSON.parse(storedData);
            } catch (e) {
                allSortData = {};
            }
        }

        allSortData[lorebookName] = sortedIds;
        localStorage.setItem(LOREBOOK_UI_SORT_KEY, JSON.stringify(allSortData));
        return true;
    } catch (error) {
        console.error('角色世界书: 保存UI排序到本地存储失败', error);
        return false;
    }
}

