// 启用拖动排序
function enableDragSort($container) {
    if (!$container.hasClass('ui-sortable')) {
        $container.sortable({
            handle: '.drag-handle',
            axis: 'y',
            containment: 'parent',
            tolerance: 'pointer',
            update: function(event, ui) {
                const $item = ui.item;
                const entryUid = $item.attr('data-entry-uid');
                const lorebookName = $item.attr('data-entry-lorebook');
                const newIndex = $item.index();

                // 收集所有条目的当前UI顺序
                const uiSortOrder = [];
                $container.find(`.${LOREBOOK_ENTRY_CLASS}`).each(function() {
                    const uid = $(this).attr('data-entry-uid');
                    if (uid) {
                        uiSortOrder.push(uid);
                    }
                });

                // 保存UI排序到本地存储
                if (uiSortOrder.length > 0) {
                    saveUISort(lorebookName, uiSortOrder);
                }

                // 保存当前滚动位置
                const parentDoc = window.parent.document;
                const $panel = $(`#${LOREBOOK_PANEL_ID}`, parentDoc);
                const $list = $panel.find(`#${LOREBOOK_LIST_CONTAINER_ID}`);
                const scrollTop = $list.scrollTop();

                // 显示操作成功提示
                if (!$container.find('.reordering-message').length) {
                    $container.append('<div class="reordering-message">UI排序已保存</div>');
                }

                // 提示消息自动消失
                setTimeout(() => {
                    $container.find('.reordering-message').fadeOut(1000, function() {
                        $(this).remove();
                    });

                    // 恢复滚动位置
                    $list.scrollTop(scrollTop);
                }, 1000);
            }
        });
    }
}

// 从本地存储加载特定世界书的UI排序
function loadUISort(lorebookName) {
    try {
        const storedData = localStorage.getItem(LOREBOOK_UI_SORT_KEY);
        if (!storedData) return null;

        const allSortData = JSON.parse(storedData);
        if (!allSortData[lorebookName]) return null;

        const numericUids = allSortData[lorebookName].map(uid => ensureNumericUID(uid));
        return numericUids;
    } catch (error) {
        console.error('角色世界书: 从本地存储加载UI排序失败', error);
        return null;
    }
}

// 根据UI排序重新排列条目
function applySavedUISort($container, lorebookName) {
    const savedSort = loadUISort(lorebookName);
    if (!savedSort || !savedSort.length) {
        return false;
    }

    const $entries = $container.find(`.${LOREBOOK_ENTRY_CLASS}`);
    if (!$entries.length) {
        return false;
    }

    // 创建一个映射以加速查找
    const entryMap = {};
    $entries.each(function() {
        const uid = $(this).attr('data-entry-uid');
        entryMap[uid] = $(this);
    });

    // 计数已排序的条目
    let sortedCount = 0;

    // 按保存的顺序重新排列条目
    for (const uid of savedSort) {
        const $entry = entryMap[uid];
        if ($entry && $entry.length) {
            $container.append($entry);
            sortedCount++;
        }
    }

    return sortedCount > 0;
}

// 修复UID类型不一致问题
function fixUIDMismatchIssue() {
    const parentDoc = window.parent.document;
    const $entries = $(`.${LOREBOOK_ENTRY_CLASS}`, parentDoc);

    $entries.each(function() {
        const $entry = $(this);
        const entryUid = $entry.attr('data-entry-uid');
        const numericUid = String(Math.floor(Number(entryUid)));

        if (entryUid !== numericUid) {
            $entry.attr('data-entry-uid', numericUid);
        }
    });

    return true;
}

// 自动保存单个字段
const saveEntryField = errorCatched(async (entryUid, lorebookName, fieldName, value) => {
    const numericUid = ensureNumericUID(entryUid);

    try {
        const entries = await TavernHelper.getLorebookEntries(lorebookName);
        if (!entries || entries.length === 0) {
            return false;
        }

        let originalEntry = null;
        for (const entry of entries) {
            const entryNumericUid = ensureNumericUID(entry.uid);
            if (entryNumericUid === numericUid) {
                originalEntry = entry;
                break;
            }
        }

        if (!originalEntry) {
            return false;
        }

        const updatedEntry = JSON.parse(JSON.stringify(originalEntry));
        updatedEntry.uid = originalEntry.uid;

        if (fieldName === 'constant') {
            updatedEntry.type = value ? 'constant' : 'selective';
        } else if (fieldName === 'type') {
            updatedEntry.type = value;
        } else {
            updatedEntry[fieldName] = value;
        }

        if (fieldName === 'probability') {
            updatedEntry.useProbability = value < 100;
        }

        await safeSetLorebookEntries(lorebookName, [updatedEntry]);
        return true;
    } catch (error) {
        console.error(`角色世界书: 保存字段时出错`, error);
        return false;
    }
}, 'saveEntryField');

/**
 * 安全地修改世界书条目，解决UID不连续时无法正确匹配的问题
 */
async function safeSetLorebookEntries(lorebookName, entriesToModify) {
  try {
    const allEntries = await TavernHelper.getLorebookEntries(lorebookName);

    const entryMap = {};
    allEntries.forEach(entry => {
      entryMap[entry.uid] = entry;
    });

    const updatedEntries = [...allEntries];

    entriesToModify.forEach(modifiedEntry => {
      const uid = modifiedEntry.uid;
      if (entryMap[uid]) {
        const index = updatedEntries.findIndex(entry => entry.uid === uid);
        if (index !== -1) {
          updatedEntries[index] = { ...updatedEntries[index], ...modifiedEntry };
        }
      }
    });

    await TavernHelper.replaceLorebookEntries(lorebookName, updatedEntries);
    return await TavernHelper.getLorebookEntries(lorebookName);
  } catch (error) {
    console.error(`安全修改世界书条目失败:`, error);
    throw error;
  }
}

async function onlyShowLast() {
  // 取消监听 防止重复触发
  eventClearListener(onlyShowLast);

  // 并行获取需要的变量
  const [chat_length, variables] = await Promise.all([getChatLength(), getVariables({ type: 'global' })]);

  // 默认只显示最后10条数据
  const showLastMessage = variables?.joezhangynShowLastMessage || 10;
  // 获取筛选关键词
  const filterKeywords = variables?.joezhangynFilterKeywords || '';

  // 确定显示范围
  let startIndex, endIndex;

  if (showLastMessage >= chat_length + 1) {
    // 显示所有条目
    startIndex = 0;
    endIndex = chat_length;
  } else {
    // 显示最后showLastMessage条
    startIndex = chat_length - showLastMessage + 1;
    endIndex = chat_length;
  }

  // 清空当前显示
  $('#chat').children().remove();

  // 如果有筛选关键词，先处理筛选逻辑
  let messagesToRender = [];

  if (filterKeywords) {
    // 遍历所有消息，找出匹配的
    for (let i = 0; i < SillyTavern.chat.length; i++) {
      const message = SillyTavern.chat[i];
      if (!message || !message.mes) continue;

      // 检查是否匹配筛选条件
      if (matchesComplexPattern(message.mes, filterKeywords)) {
        // 创建消息的深拷贝，这样我们不会修改原始消息
        const modifiedMessage = JSON.parse(JSON.stringify(message));

        // 高亮显示匹配的关键词
        modifiedMessage.mes = highlightMatchedKeywords(modifiedMessage.mes, filterKeywords);

        messagesToRender.push({ index: i, message: modifiedMessage, originalMessage: message });
      } else if (i >= startIndex && i <= endIndex) {
        // 如果不匹配但在显示范围内，也要渲染
        messagesToRender.push({ index: i, message });
      }
    }

    // 按索引排序，保持消息顺序
    messagesToRender.sort((a, b) => a.index - b.index);
  } else {
    // 没有筛选条件，使用原始逻辑
    for (let i = startIndex; i <= endIndex; i++) {
      messagesToRender.push({ index: i, message: SillyTavern.chat[i] });
    }
  }

  // 渲染消息
  for (const item of messagesToRender) {
    builtin.addOneMessage(item.message, { forceId: item.index });
    eventEmit(
      item.message.is_user ? tavern_events.USER_MESSAGE_RENDERED : tavern_events.CHARACTER_MESSAGE_RENDERED,
      item.index,
    );
  }

  // 恢复监听
  restoreListen();
}

// 提取所有可能的关键词
function extractAllKeywords(pattern) {
  // 简单移除所有括号和操作符
  return pattern
    .replace(/\(/g, ' ')
    .replace(/\)/g, ' ')
    .split(/[&|]/)
    .map(keyword => keyword.trim())
    .filter(keyword => keyword.length > 0);
}

// 高亮匹配的关键词（新函数）
function highlightMatchedKeywords(text, pattern) {
  // 提取所有原子关键词
  const keywords = extractAllKeywords(pattern);

  // 为每个关键词检查是否匹配，并高亮真正匹配的部分
  let result = text;

  // 按照关键词长度降序排序，避免短词替换影响长词匹配
  keywords.sort((a, b) => b.length - a.length);

  for (const keyword of keywords) {
    // 检查这个关键词是否匹配
    const escapedKeyword = keyword.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
    const regex = new RegExp(escapedKeyword, 'i');

    if (regex.test(result)) {
      // 如果匹配，则高亮所有出现的地方
      const highlightRegex = new RegExp(`(${escapedKeyword})`, 'gi');
      result = result.replace(highlightRegex, '<font color=#F4A460 size=10><b><u><i>$1</i></u></b></font>');
    }
  }

  return result;
}

// 复杂模式匹配函数
function matchesComplexPattern(text, pattern) {
  // 处理完整的复杂表达式
  function evaluateExpression(expr, text) {
    // 去除最外层的括号
    expr = expr.trim();
    while (expr.startsWith('(') && expr.endsWith(')') && isBalancedParens(expr.slice(1, -1))) {
      expr = expr.slice(1, -1).trim();
    }

    // 解析表达式树
    const tokens = tokenize(expr);
    return evaluateTokens(tokens, text);
  }

  // 检查括号是否平衡
  function isBalancedParens(str) {
    let count = 0;
    for (let char of str) {
      if (char === '(') count++;
      if (char === ')') count--;
      if (count < 0) return false;
    }
    return count === 0;
  }

  // 分词，将表达式分解为标记
  function tokenize(expr) {
    const tokens = [];
    let current = '';
    let parenCount = 0;

    for (let i = 0; i < expr.length; i++) {
      const char = expr[i];

      if (char === '(') {
        parenCount++;
        current += char;
      } else if (char === ')') {
        parenCount--;
        current += char;
      } else if (parenCount === 0 && (char === '&' || char === '|')) {
        if (current.trim()) {
          tokens.push(current.trim());
        }
        tokens.push(char);
        current = '';
      } else {
        current += char;
      }
    }

    if (current.trim()) {
      tokens.push(current.trim());
    }

    return tokens;
  }

  // 评估标记化的表达式
  function evaluateTokens(tokens, text) {
    // 处理单个标记
    if (tokens.length === 1) {
      const token = tokens[0];
      // 如果是括号表达式，递归处理
      if (token.startsWith('(') && token.endsWith(')')) {
        return evaluateExpression(token, text);
      }
      // 否则是简单关键词
      const regex = new RegExp(token.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'), 'i');
      return regex.test(text);
    }

    // 处理多个标记，首先处理 & 操作符（优先级更高）
    for (let i = 0; i < tokens.length; i++) {
      if (tokens[i] === '&') {
        const left = tokens.slice(0, i);
        const right = tokens.slice(i + 1);
        return evaluateTokens(left, text) && evaluateTokens(right, text);
      }
    }

    // 然后处理 | 操作符
    for (let i = 0; i < tokens.length; i++) {
      if (tokens[i] === '|') {
        const left = tokens.slice(0, i);
        const right = tokens.slice(i + 1);
        return evaluateTokens(left, text) || evaluateTokens(right, text);
      }
    }

    // 如果没有操作符，这不应该发生
    console.error('Unexpected tokens:', tokens);
    return false;
  }

  try {
    return evaluateExpression(pattern, text);
  } catch (e) {
    console.error('Error parsing pattern:', e);
    // 降级到简单匹配
    const simpleRegex = new RegExp(pattern.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'), 'i');
    return simpleRegex.test(text);
  }
}

