// 基础配置项
var option;
(function (option_1) {
    const default_option = {
        should_send_directly: true,
        init_delay: 0,
        travel_mode: true, 
    };

    option_1.option = {...default_option}; 

    async function parse_option() {
        try {
            const result = {...default_option};

            let entries;
            try {
                entries = await getLorebookEntries(lorebook_name);

                entries = entries.filter(entry => entry.comment && entry.comment.startsWith('设置-'));
            } catch (err) {
                console.error('获取世界书条目失败:', err);
                return result;
            }

            let foundSendSetting = false;

            const sendSettingEntry = entries.find(entry => 
                entry.comment === '设置-开启则直接发送，关闭则填在输入框'
            );

            if (sendSettingEntry) {
                foundSendSetting = true;
                const shouldSendDirectly = sendSettingEntry.enabled === true;
                result.should_send_directly = shouldSendDirectly;
            }

            const delayEntry = entries.find(entry => entry.comment === '设置-初始化延迟');
            if (delayEntry) {
                const delay = delayEntry.content && !isNaN(Number(delayEntry.content)) 
                    ? Number(delayEntry.content) 
                    : 0;
                result.init_delay = delay;
            }

            // 移除"移动模式"设置，固定为写实模式
            result.travel_mode = true;

            if (!foundSendSetting) {
                console.warn('未找到"开启则直接发送，关闭则填在输入框"设置，使用默认值:', result.should_send_directly);
            }

            return result;
        } catch (error) {
            console.error("解析设置出错:", error);
            return {...default_option};
        }
    }

    async function update() {
        try {
            const old_option = option_1.option;
            option_1.option = await parse_option();
            return !_.isEqual(option_1.option, old_option);
        } catch (error) {
            console.error("更新设置出错:", error);
            if (!option_1.option) {
                option_1.option = {...default_option};
            }
            return false;
        }
    }

    option_1.update = update;
})(option || (option = {}));

