                const connectingEdges = mapData.edges.filter(edge => 
                    ((edge.from === currentLocationId && edge.to === nodeId) ||
                     (edge.to === currentLocationId && edge.from === nodeId))
                );

                isReachable = connectingEdges.length > 0;
            }

            if (!isReachable) {
                if (mapData.free_movement === "F") {
                    $container.find('.selected_location_display').html('<i class="fa-solid fa-ban"></i> 无法前往该地点');
                } else {
                    $container.find('.selected_location_display').html('<i class="fa-solid fa-ban"></i> 只能前往相邻地点');
                }
                return;
            }

            const connectingEdges = mapData.edges.filter(edge => 
                ((edge.from === currentLocationId && edge.to === nodeId) ||
                    (edge.to === currentLocationId && edge.from === nodeId))
            );

            const allPathsLocked = isReachable && mapData.free_movement !== "F" && 
                connectingEdges.length > 0 && 
                connectingEdges.every(edge => isLockedEdgeType(edge.type));

            selectedLocation = nodeId;

            let hasLockedPath = false;
            let hasOpenPath = false;

            if (mapData.currentLocation) {
                hasLockedPath = connectingEdges.some(edge => isLockedEdgeType(edge.type));
                hasOpenPath = connectingEdges.some(edge => !isLockedEdgeType(edge.type));
            }

            let displayText = `<i class="fa-solid fa-map-pin"></i> 已选择: ${targetNode.label}`;

            if (allPathsLocked) {
                let lockType = 'l';

                for (const edge of connectingEdges) {
                    if (isLockedEdgeType(edge.type)) {
                        lockType = edge.type;
                        break;
                    }
                }

                switch(lockType) {
                    case 'ld':
                        displayText += ` <i class="fa-solid fa-lock"></i> (门已锁住)`;
                        break;
                    case 'lg':
                        displayText += ` <i class="fa-solid fa-user-shield"></i> (有人守卫)`;
                        break;
                    case 'lb':
                        displayText += ` <i class="fa-solid fa-ban"></i> (通道被阻挡)`;
                        break;
                    case 'lh':
                        displayText += ` <i class="fa-solid fa-eye-slash"></i> (入口隐藏)`;
                        break;
                    default:
                        displayText += ` <i class="fa-solid fa-lock"></i> (无法到达)`;
                }
            } else if (hasLockedPath && hasOpenPath) {
                displayText += ` <i class="fa-solid fa-info-circle"></i> (部分路径无法通行)`;
            }

            $container.find('.selected_location_display').html(displayText);
            $container.find('.go_button').removeClass('disabled');
        }
    }

