        const movementMode = mapData.free_movement;
        if (movementMode === "F") {
            titleText += ' <i class="fa-solid fa-arrows-alt"></i> [自由移动模式]';
        } else if (movementMode === "A") {
            titleText += ' <i class="fa-solid fa-route"></i> [临近移动模式]';
        } else if (movementMode === "N") {
