        }

        $titleBar.append(`<h3>${titleText}</h3>`);
        $mapContainer.append($titleBar);

        const $monitorScreen = $('<div class="monitor-screen" id="monitorScreen">');
        const $mapCanvas = $('<canvas id="mapCanvas"></canvas>');

        const $selectedLocation = $('<div class="selected_location_display"><i class="fa-solid fa-map-marker-alt"></i> 选择一个位置以前往</div>');

        $monitorScreen.append($mapCanvas, $selectedLocation);
        $mapContainer.append($monitorScreen);

        const $actionArea = $('<div class="map_action_area">');
        const $goButton = $('<button class="go_button disabled"><i class="fa-solid fa-person-walking"></i> 前往选中地点</button>')
            .on('click', function() {
                if (!$(this).hasClass('disabled')) {
                    goToLocation($(this), mapData);
                }
            });
        $actionArea.append($goButton);
        $mapContainer.append($actionArea);

        $container.append($mapContainer);

        $container.append(styleContent);

        setTimeout(() => {
            initCanvas($container, mapData);
        }, 100);

        return $container;
    }
    render_1.extract_map_element = extract_map_element;

    // 初始化画布和绘图
    function initCanvas($container, mapData) {
        const canvas = $container.find('#mapCanvas')[0];
        const monitorScreen = $container.find('#monitorScreen')[0];
        if (!canvas || !monitorScreen) return;

        const iframeElement = canvas.ownerDocument.defaultView.frameElement;
        const ctx = canvas.getContext('2d');

        let offsetX = 0, offsetY = 0, scale = 1;
        let isDragging = false, dragStartX = 0, dragStartY = 0, initialOffsetX = 0, initialOffsetY = 0;
        const minScale = 0.2, maxScale = 4.0, zoomSensitivity = 0.1;

        // 多点触控处理
        let lastTouchDistance = 0;
        let touchZoomActive = false;
        let touchZoomStartScale = 1;
        let touchZoomCenter = { x: 0, y: 0 };
        let lastTouchCenter = { x: 0, y: 0 };
        let touchStartTime = 0;

        // 绘制动画帧ID
        let animationFrameId = null;

        // 设置画布尺寸
        function resizeCanvas() {
            const dpr = window.devicePixelRatio || 1;
            const rect = canvas.getBoundingClientRect();
            canvas.width = rect.width * dpr;
            canvas.height = rect.height * dpr;
            drawMap();
        }

        // 视图变换
        function applyViewTransform() {
            const dpr = window.devicePixelRatio || 1;
            const cw = canvas.width / dpr;
            const ch = canvas.height / dpr;

            ctx.setTransform(
                scale * dpr, 0, 0, scale * dpr,
                (scale * offsetX + cw / 2) * dpr,
                (scale * offsetY + ch / 2) * dpr
            );
        }

        // 屏幕坐标转世界坐标
        function screenToWorld(screenX, screenY) {
            const dpr = window.devicePixelRatio || 1;
            const cw = canvas.width / dpr;
            const ch = canvas.height / dpr;

            return {
                x: (screenX - cw / 2) / scale - offsetX,
                y: (screenY - ch / 2) / scale - offsetY
            };
        }

        // 点击地图检测节点
        function checkNodeClick(x, y) {
            const worldCoords = screenToWorld(x, y);
            const worldX = worldCoords.x;
            const worldY = worldCoords.y;

            // 检查是否点击了节点
            for (const node of mapData.nodes) {
                let isInside = false;

                if (node.shape === 'r') {
                    const w = node.width || 40;
                    const h = node.height || 30;
                    isInside = 
                        worldX >= node.x - w / 2 && 
                        worldX <= node.x + w / 2 && 
                        worldY >= node.y - h / 2 && 
                        worldY <= node.y + h / 2;
                } else if (node.shape === 'c') {
                    const r = node.radius || 15;
                    const dx = worldX - node.x;
                    const dy = worldY - node.y;
                    isInside = dx * dx + dy * dy <= r * r;
                }

                if (isInside) {
                    locationClick($container, mapData, node.id);
                    return true;
                }
            }

            return false;
        }

        // 计算可到达节点
        function getReachableNodes() {
            if (!mapData.currentLocation) {
                return [];
            }

            // 检查移动模式
            const movementMode = mapData.free_movement;

            // 禁止移动模式：只有当前位置可点击
            if (movementMode === "N") {
                return [mapData.currentLocation];
            }
            // 自由移动模式：所有节点都可到达
            else if (movementMode === "F") {
                const reachableNodes = new Set([mapData.currentLocation]);
                mapData.nodes.forEach(node => {
                    reachableNodes.add(node.id);
                });
                return Array.from(reachableNodes);
            } 
            // 临近移动模式：只能到达当前位置相邻的节点
            else {
                const reachableNodes = new Set([mapData.currentLocation]);

                mapData.edges.forEach(edge => {
                    if (!isLockedEdgeType(edge.type)) {
                        if (edge.from === mapData.currentLocation) {
                            reachableNodes.add(edge.to);
                        } else if (edge.to === mapData.currentLocation) {
                            reachableNodes.add(edge.from);
                        }
                    }
                });

                return Array.from(reachableNodes);
            }
        }

        // 重置视图
        function resetView() {
            scale = 1;
            if (mapData && mapData.nodes.length > 0) {
                let minX = Infinity, minY = Infinity, maxX = -Infinity, maxY = -Infinity;
                mapData.nodes.forEach(n => {
                    minX = Math.min(minX, n.x);
                    minY = Math.min(minY, n.y);
                    maxX = Math.max(maxX, n.x);
                    maxY = Math.max(maxY, n.y);
                });

                offsetX = -((minX + maxX) / 2);
                offsetY = -((minY + maxY) / 2);
            } else {
                offsetX = 0;
                offsetY = 0;
            }
        }

        // 从CSS变量中获取颜色
        function getCSSColor(property, fallback) {
            let value = '';

            const containers = [
                $container[0],
                document.querySelector('.map_graph'),
                document.querySelector('#map_container'),
                document.querySelector('.map-container'),
                document.querySelector('.map_view'),
                document.querySelector('#map_view'),
                document.querySelector('[class*="map"]')
            ];

            for (const container of containers) {
                if (container) {
                    const style = getComputedStyle(container);
                    value = style.getPropertyValue(property).trim();
                    if (value) break;
                }
            }

            if (!value) {
                value = getComputedStyle(document.body).getPropertyValue(property).trim() || 
                       getComputedStyle(document.documentElement).getPropertyValue(property).trim();
            }

            return value || fallback;
        }

        // 绘制地图
        function drawMap() {
            if (!mapData) return;

            const dpr = window.devicePixelRatio || 1;
            const canvasWidth = canvas.width / dpr;
            const canvasHeight = canvas.height / dpr;

            ctx.save();

            ctx.setTransform(dpr, 0, 0, dpr, 0, 0);
            ctx.clearRect(0, 0, canvasWidth * dpr, canvasHeight * dpr);

            applyViewTransform();

            const reachableNodeIds = getReachableNodes();

            const nodesById = new Map(mapData.nodes.map(node => [node.id, node]));

            const edgeMainColor = getCSSColor('--edge-main-color', '#00aaaa80');
            const edgeReachableColor = getCSSColor('--edge-reachable-color', '#00ffff80');
            const edgeLockedColor = getCSSColor('--edge-locked-color', '#77777780');

            const mainColor = getCSSColor('--node-main-color', '#00aaaa');
            const selectedColor = getCSSColor('--node-selected-color', '#00ffff');
            const currentLocationColor = getCSSColor('--node-current-color', '#80ffff');
            const lockedColor = getCSSColor('--node-locked-color', '#777777');

            const edgesToDraw = [];
            const nodesToDraw = [];
            const labelsToDraw = [];

            const nodeAccessibility = new Map();

            mapData.nodes.forEach(node => {
                nodeAccessibility.set(node.id, {
                    isReachable: false,
                    allPathsLocked: false,
                    somePathsLocked: false,
                    hasOpenPath: false
                });
            });

            if (mapData.currentLocation) {
                const currentNodeInfo = nodeAccessibility.get(mapData.currentLocation);
                if (currentNodeInfo) {
                    currentNodeInfo.isReachable = true;
                    currentNodeInfo.hasOpenPath = true;
                }

                mapData.edges.forEach(edge => {
                    let targetNodeId = null;

                    if (edge.from === mapData.currentLocation) {
                        targetNodeId = edge.to;
                    } else if (edge.to === mapData.currentLocation) {
                        targetNodeId = edge.from;
                    }

                    if (targetNodeId) {
                        const nodeInfo = nodeAccessibility.get(targetNodeId);
                        if (nodeInfo) {
                            nodeInfo.isReachable = true;

                            if (edge.type === 'l') {
                                nodeInfo.somePathsLocked = true;
                            } else {
                                nodeInfo.hasOpenPath = true;
                            }
                        }
                    }
                });

                mapData.nodes.forEach(node => {
                    if (node.id === mapData.currentLocation) return;

                    const connectingEdges = mapData.edges.filter(edge => 
                        ((edge.from === mapData.currentLocation && edge.to === node.id) ||
                         (edge.to === mapData.currentLocation && edge.from === node.id))
                    );

                    if (connectingEdges.length > 0) {
                        const nodeInfo = nodeAccessibility.get(node.id);
                        if (nodeInfo) {
                            nodeInfo.allPathsLocked = connectingEdges.every(edge => edge.type === 'l');
                        }
                    }
                });
            }

            // 处理节点数据
            mapData.nodes.forEach(node => {
                const isSelected = node.id === selectedLocation;
                const isCurrent = node.id === mapData.currentLocation;

                const nodeInfo = nodeAccessibility.get(node.id) || {
                    isReachable: false,
                    allPathsLocked: false,
                    somePathsLocked: false,
                    hasOpenPath: false
                };

                // 确定节点可达状态
                let isReachable = nodeInfo.isReachable;
                const isEffectivelyLocked = nodeInfo.allPathsLocked && !nodeInfo.hasOpenPath;
                const isNotReachable = !isReachable && !isCurrent;

                // 获取节点颜色
                let nodeColor = mainColor;
                if (isSelected) nodeColor = selectedColor;
                if (isCurrent) nodeColor = currentLocationColor;
                if (isEffectivelyLocked) nodeColor = lockedColor;

                if (isNotReachable && mapData.free_movement !== "F") {
                    nodeColor = lockedColor;
                }

                // 添加节点到绘制列表
                nodesToDraw.push({ 
                    node, 
                    color: nodeColor, 
                    shadowBlur: isCurrent ? 10 : isSelected ? 8 : 4,
                    isSelected,
                    isCurrent,
                    isReachable,
                    isEffectivelyLocked,
                    isNodeLocked: false,
                    allPathsLocked: nodeInfo.allPathsLocked,
                    somePathsLocked: nodeInfo.somePathsLocked,
                    hasOpenPath: nodeInfo.hasOpenPath,
                    isNotReachable,
                    isExitNode: isExitNodeType(node.type)
                });

                // 处理标签
                if (node.label) {
                    const labelText = node.label;
                    const labelColor = '#aaaaaa';

                    // 计算字体大小
                    const paddingX = 4;
                    const paddingY = 2;
                    let innerWidth, innerHeight;

                    if (node.shape === 'r') {
                        innerWidth = (node.width || 40) - paddingX * 2;
                        innerHeight = (node.height || 30) - paddingY * 2;
                    } else {
                        const diameter = (node.radius || 15) * 2;
                        innerWidth = diameter - paddingX * 2;
                        innerHeight = Math.min(diameter - paddingY * 2, innerWidth);
                        innerWidth = innerHeight;
                    }

                    innerWidth = Math.max(1, innerWidth);
                    innerHeight = Math.max(1, innerHeight);

                    // 基准字体大小
                    let fitWorldFontSize = 8;

                    ctx.font = `${fitWorldFontSize}px var(--font-family-main, "Segoe UI"), sans-serif`;
                    const metrics = ctx.measureText(labelText);
                    const measuredWidth = metrics.width;

                    if (measuredWidth > 1e-6) {
                        const fontSizeBasedOnWidth = fitWorldFontSize * (innerWidth / measuredWidth);
                        fitWorldFontSize = Math.min(fontSizeBasedOnWidth, innerHeight);
                    } else {
                        fitWorldFontSize = innerHeight;
                    }

                    const potentialScreenSize = fitWorldFontSize * scale * dpr;
                    const clampedScreenSize = Math.max(5 * dpr, Math.min(10 * dpr, potentialScreenSize));
                    const finalWorldFontSize = clampedScreenSize / (scale * dpr);

                    if (finalWorldFontSize > 0.1) {
                        labelsToDraw.push({
                            text: labelText,
                            x: node.x,
                            y: node.y,
                            color: labelColor,
                            fontSize: finalWorldFontSize
                        });
                    }
                }
            });

            // 处理边数据
            mapData.edges.forEach(edge => {
                const fromNode = nodesById.get(edge.from);
                const toNode = nodesById.get(edge.to);

                if (fromNode && toNode) {
                    const startPoint = getIntersectionPointWithShape(toNode.x, toNode.y, fromNode);
                    const endPoint = getIntersectionPointWithShape(fromNode.x, fromNode.y, toNode);

                    const isLocked = isLockedEdgeType(edge.type);
                    const isCurrentPath = (edge.from === mapData.currentLocation || edge.to === mapData.currentLocation);
                    let edgeColor = isLocked ? edgeLockedColor : edgeMainColor;

                    if (isCurrentPath && !isLocked) {
                        edgeColor = edgeReachableColor;
                    }

                    edgesToDraw.push({ 
                        startPoint, 
                        endPoint, 
                        color: edgeColor, 
                        dashed: isLocked,
                        isCurrentPath,
                        isLocked,
                        shadowBlur: isCurrentPath ? 10 : 8,
                        edgeType: edge.type
                    });
                }
            });

            edgesToDraw.forEach(({ startPoint, endPoint, color, dashed, isCurrentPath, isLocked, shadowBlur, edgeType }) => {
                ctx.beginPath();

                ctx.moveTo(startPoint.x, startPoint.y);
                ctx.lineTo(endPoint.x, endPoint.y);

                if (isLocked) {
                    ctx.strokeStyle = isCurrentPath ? '#ff550080' : '#ff770080';
                } else {
                    ctx.strokeStyle = isCurrentPath ? '#00ffff80' : '#00aaaa80';
                }

                ctx.shadowColor = ctx.strokeStyle;
                ctx.shadowBlur = dashed ? 4 : 8;

                if (dashed) {
                    ctx.setLineDash([5 * dpr / scale, 3 * dpr / scale]);
                    ctx.lineWidth = ctx.lineWidth * 1.2;
                } else {
                    ctx.setLineDash([]);
                }

                ctx.lineWidth = Math.max(ctx.lineWidth, 1 / scale);

                ctx.stroke();

                if (isLocked) {
                    const midX = (startPoint.x + endPoint.x) / 2;
                    const midY = (startPoint.y + endPoint.y) / 2;
                    const size = 6 / scale;

                    ctx.beginPath();
                    ctx.arc(midX, midY, size + 3, 0, Math.PI * 2);
                    ctx.fillStyle = 'rgba(0,0,0,0.7)';
                    ctx.fill();

                    let iconCode = '\uf023'; 
                    if (edgeType && isLockedEdgeType(edgeType)) {
                        iconCode = getEdgeLockIconCode(edgeType);
                    }

                    ctx.font = `${size * 1.8}px "FontAwesome", sans-serif`;
                    ctx.fillStyle = 'rgba(255, 200, 0, 0.9)';
                    ctx.textAlign = 'center';
                    ctx.textBaseline = 'middle';
                    ctx.shadowColor = 'rgba(255, 150, 0, 0.7)';
                    ctx.shadowBlur = 5;

                    ctx.fillText(iconCode, midX, midY);

                    ctx.shadowBlur = 0;
                    ctx.shadowColor = 'transparent';
                }

                ctx.setLineDash([]);
                ctx.shadowBlur = 0;
                if (dashed) {
                    ctx.lineWidth = ctx.lineWidth / 1.2;
                }
            });

            // 绘制节点
            nodesToDraw.forEach(({ 
                node, color, shadowBlur, isSelected, isCurrent, isReachable, 
                isEffectivelyLocked, isNodeLocked, allPathsLocked, somePathsLocked, hasOpenPath, isNotReachable, isExitNode
            }) => {
                ctx.strokeStyle = color;
                ctx.shadowColor = color;
                ctx.shadowBlur = shadowBlur;

                // 绘制节点轮廓与填充
                ctx.beginPath();
                if (node.shape === 'r') {
                    const w = node.width || 40;
                    const h = node.height || 30;
                    ctx.rect(node.x - w / 2, node.y - h / 2, w, h);
                } else if (node.shape === 'c') {
                    const r = node.radius || 15;
                    ctx.arc(node.x, node.y, r, 0, Math.PI * 2);
                }

                // 添加半透明填充
                ctx.fillStyle = `${color}40`;
                ctx.fill();
                ctx.stroke();

                // 如果是选中位置，添加边框效果
                if (isSelected) {
                    ctx.shadowBlur = 15;
                    ctx.shadowColor = '#00ffff';

                    ctx.lineWidth = ctx.lineWidth * 1.5;
                    ctx.stroke();

                    ctx.shadowBlur = 0;
                    ctx.shadowColor = 'transparent';
                    ctx.lineWidth = ctx.lineWidth / 1.5;
                }

                // 如果是出口节点，添加出口图标
                if (isExitNode) {
                    const fontSize = 14 / scale;

                    ctx.font = `${fontSize}px "FontAwesome"`;
                    ctx.fillStyle = getCSSColor('--exit-node-icon-color', '#00ffaa');
                    ctx.textAlign = 'center';
                    ctx.textBaseline = 'middle';
                    ctx.shadowColor = 'rgba(0, 255, 170, 0.7)';
                    ctx.shadowBlur = 4;

                    ctx.fillText(getNodeExitIconCode('e'), node.x, node.y);

                    ctx.shadowBlur = 0;
                    ctx.shadowColor = 'transparent';
                }

                // 如果是锁定的房间，添加锁的标志
                if (isEffectivelyLocked) {
                    const fontSize = 10 / scale;
                    const iconOffsetY = node.shape === 'r' ? (node.height || 30) / 2 : (node.radius || 15);

                    // 绘制锁定状态的背景
                    ctx.fillStyle = 'rgba(0,0,0,0.5)';
                    ctx.beginPath();
                    if (node.shape === 'r') {
                        ctx.rect(
                            node.x - (node.width || 40) / 2,
                            node.y - (node.height || 30) / 2,
                            node.width || 40,
                            node.height || 30
                        );
                    } else {
                        ctx.arc(node.x, node.y, node.radius || 15, 0, Math.PI * 2);
                    }
                    ctx.fill();

                    // 绘制锁图标
                    const iconCode = '\uf023'; // 锁图标
                    const statusText = '无法到达';

                    ctx.font = `${fontSize}px "FontAwesome"`;
                    ctx.fillStyle = lockedColor;
                    ctx.textAlign = 'center';
                    ctx.textBaseline = 'middle';
                    ctx.shadowColor = lockedColor;
                    ctx.shadowBlur = 3;
                    ctx.fillText(iconCode, node.x, node.y);

                    // 绘制状态文本
                    const lockTextFont = Math.max(8, fontSize * 0.8) / scale;
                    ctx.font = `${lockTextFont}px var(--font-family-main, "Segoe UI"), sans-serif`;
                    ctx.fillStyle = '#aaaaaa';
                    ctx.fillText(statusText, node.x, node.y + iconOffsetY / 2);

                    ctx.shadowBlur = 0;
                    ctx.shadowColor = 'transparent';
                }

                ctx.shadowBlur = 0;
            });

            // 绘制标签
            labelsToDraw.forEach(({ text, x, y, color, fontSize }) => {
                ctx.font = `${fontSize}px var(--font-family-main, "Segoe UI"), sans-serif`;
                ctx.fillStyle = color;
                ctx.shadowColor = color;
                ctx.shadowBlur = 2;
                ctx.textAlign = 'center';
                ctx.textBaseline = 'middle';
                ctx.fillText(text, x, y);

                ctx.shadowColor = "transparent";
                ctx.shadowBlur = 0;
            });

            ctx.restore();

            // 更新选中地点的显示
            if (selectedLocation) {
                const selectedNode = mapData.nodes.find(node => node.id === selectedLocation);
                if (selectedNode) {
                    $container.find('.selected_location_display').html(`<i class="fa-solid fa-map-pin"></i> 已选择: ${selectedNode.label}`);
                    $container.find('.go_button').removeClass('disabled');
                }
            } else {
                $container.find('.selected_location_display').html('<i class="fa-solid fa-map-marker-alt"></i> 选择一个位置以前往');
                $container.find('.go_button').addClass('disabled');
            }

            // 请求下一帧，保持闪烁效果
            if (mapData.currentLocation || selectedLocation) {
                // 取消之前的动画帧请求
                if (animationFrameId) {
                    cancelAnimationFrame(animationFrameId);
                }
                animationFrameId = requestAnimationFrame(drawMap);
            }
        }

        // 添加点击事件监听
        $(monitorScreen).on('click', function(e) {
            if (isDragging) return;  

            const canvasRect = canvas.getBoundingClientRect();

            let clickX, clickY;

            if (iframeElement) {
                const iframeRect = iframeElement.getBoundingClientRect();
                const absX = e.originalEvent.clientX - iframeRect.left;
                const absY = e.originalEvent.clientY - iframeRect.top;
                clickX = absX - canvasRect.left;
                clickY = absY - canvasRect.top;
            } else {
                clickX = e.originalEvent.clientX - canvasRect.left;
                clickY = e.originalEvent.clientY - canvasRect.top;
            }

            checkNodeClick(clickX, clickY);
        });

        monitorScreen.ontouchstart = function(e) {
            if (e.touches.length === 2) {
                e.preventDefault();

                touchStartTime = Date.now();

                const rect = canvas.getBoundingClientRect();

                let centerX, centerY, distance;

                if (iframeElement) {
                    const iframeRect = iframeElement.getBoundingClientRect();
                    const touch1 = {
                        x: e.touches[0].clientX - iframeRect.left,
                        y: e.touches[0].clientY - iframeRect.top
                    };
                    const touch2 = {
                        x: e.touches[1].clientX - iframeRect.left,
                        y: e.touches[1].clientY - iframeRect.top
                    };

                    centerX = (touch1.x + touch2.x) / 2;
                    centerY = (touch1.y + touch2.y) / 2;

                    const dx = touch2.x - touch1.x;
                    const dy = touch2.y - touch1.y;
                    distance = Math.sqrt(dx * dx + dy * dy);
                } else {
                    const touch1 = e.touches[0];
                    const touch2 = e.touches[1];

                    centerX = (touch1.clientX + touch2.clientX) / 2;
                    centerY = (touch1.clientY + touch2.clientY) / 2;

                    const dx = touch2.clientX - touch1.clientX;
                    const dy = touch2.clientY - touch1.clientY;
                    distance = Math.sqrt(dx * dx + dy * dy);
                }

                lastTouchDistance = distance;
                touchZoomActive = true;
                touchZoomStartScale = scale;

                touchZoomCenter = {
                    x: centerX - rect.left,
                    y: centerY - rect.top
                };

                lastTouchCenter = {
                    x: centerX,
                    y: centerY
                };

                return;
            }

            if (e.touches.length !== 1) return;
            if (e.target.tagName === 'BUTTON') return;

            e.preventDefault(); 

            const rect = canvas.getBoundingClientRect();

            let touchX, touchY;

            if (iframeElement) {
                const iframeRect = iframeElement.getBoundingClientRect();
                const touch = e.touches[0];
                touchX = touch.clientX - iframeRect.left;
                touchY = touch.clientY - iframeRect.top;
            } else {
                const touch = e.touches[0];
                touchX = touch.clientX;
                touchY = touch.clientY;
            }

            dragStartX = touchX - rect.left;
            dragStartY = touchY - rect.top;
            initialOffsetX = offsetX;
            initialOffsetY = offsetY;

            isDragging = true;

            let touchStartPoint = {
                x: dragStartX,
                y: dragStartY,
                time: Date.now()
            };
        };

        monitorScreen.ontouchend = function(e) {
            if (isDragging) {
                const rect = canvas.getBoundingClientRect();
                const touchEndX = e.changedTouches[0].clientX;
                const touchEndY = e.changedTouches[0].clientY;

                let touchX, touchY;
                const touchEndPoint = {
                    time: Date.now()
                };

                if (iframeElement) {
                    const iframeRect = iframeElement.getBoundingClientRect();
                    touchX = touchEndX - iframeRect.left - rect.left;
                    touchY = touchEndY - iframeRect.top - rect.top;
                } else {
                    touchX = touchEndX - rect.left;
                    touchY = touchEndY - rect.top;
                }

                const moveDistance = Math.sqrt(
                    Math.pow(touchX - dragStartX, 2) + 
                    Math.pow(touchY - dragStartY, 2)
                );

                if (moveDistance < 10) {
                    checkNodeClick(touchX, touchY);
                }

                isDragging = false;
            }
        };

        let lastZoomUpdateTime = 0;
        const throttleTime = 16;

        const parentDoc = window.parent.document;

        $(parentDoc).on('touchmove.mapTouch', function(e) {
            if (touchZoomActive && e.originalEvent.touches.length === 2) {
                e.preventDefault();

                const now = Date.now();
                if (now - lastZoomUpdateTime < throttleTime) {
                    return;
                }
                lastZoomUpdateTime = now;

                let centerX, centerY, distance;

                if (iframeElement) {
                    const iframeRect = iframeElement.getBoundingClientRect();
                    const touch1 = {
                        x: e.originalEvent.touches[0].clientX - iframeRect.left,
                        y: e.originalEvent.touches[0].clientY - iframeRect.top
                    };
                    const touch2 = {
                        x: e.originalEvent.touches[1].clientX - iframeRect.left,
                        y: e.originalEvent.touches[1].clientY - iframeRect.top
                    };

                    centerX = (touch1.x + touch2.x) / 2;
                    centerY = (touch1.y + touch2.y) / 2;

                    const dx = touch2.x - touch1.x;
                    const dy = touch2.y - touch1.y;
                    distance = Math.sqrt(dx * dx + dy * dy);
                } else {
                    const touch1 = e.originalEvent.touches[0];
                    const touch2 = e.originalEvent.touches[1];

                    centerX = (touch1.clientX + touch2.clientX) / 2;
                    centerY = (touch1.clientY + touch2.clientY) / 2;

                    const dx = touch2.clientX - touch1.clientX;
                    const dy = touch2.clientY - touch1.clientY;
                    distance = Math.sqrt(dx * dx + dy * dy);
                }

                // 计算中心点的移动
                const centerDeltaX = centerX - lastTouchCenter.x;
                const centerDeltaY = centerY - lastTouchCenter.y;

                // 更新上一次中心位置
                lastTouchCenter.x = centerX;
                lastTouchCenter.y = centerY;

                // 计算缩放因子
                if (lastTouchDistance <= 0 || distance <= 0) {
                    return;
                }

                const touchDuration = now - touchStartTime;
                let scaleFactor;

                // 根据触摸持续时间调整敏感度
                if (touchDuration < 300) {
                    scaleFactor = distance / lastTouchDistance;
                    scaleFactor = 1 + (scaleFactor - 1) * 0.3; 
                } else {
                    scaleFactor = distance / lastTouchDistance;
                    scaleFactor = 1 + (scaleFactor - 1) * 0.5;
                }

                // 微小变化忽略处理
                if (Math.abs(scaleFactor - 1) < 0.01) {
                    // 仅处理平移
                    offsetX += centerDeltaX / scale;
                    offsetY += centerDeltaY / scale;
                    drawMap();
                    return;
                }

                // 计算新缩放值
                const newScale = Math.max(minScale, Math.min(maxScale, scale * scaleFactor));

                // 计算画布中心点的世界坐标
                const canvasRect = canvas.getBoundingClientRect();
                const dpr = window.devicePixelRatio || 1;
                const cw = canvas.width / dpr;
                const ch = canvas.height / dpr;

                // 使用touchZoomCenter（相对canvas的固定点）进行缩放
                const zoomPointX = touchZoomCenter.x;
                const zoomPointY = touchZoomCenter.y;

                // 当前缩放下，缩放点在世界坐标中的位置
                const worldX = (zoomPointX - cw / 2) / scale - offsetX;
                const worldY = (zoomPointY - ch / 2) / scale - offsetY;

                // 应用新缩放
                const oldScale = scale;
                scale = newScale;

                // 保持缩放点位置不变
                offsetX = -worldX + zoomPointX / scale - cw / (2 * scale);
                offsetY = -worldY + zoomPointY / scale - ch / (2 * scale);

                // 同时考虑中心点的移动
                offsetX += centerDeltaX / scale;
                offsetY += centerDeltaY / scale;

                // 更新上一次触摸距离
                lastTouchDistance = distance;

                // 重绘地图
                drawMap();
                return;
            }

            // 单指拖拽
            if (!isDragging || e.originalEvent.touches.length !== 1) return;

            e.preventDefault(); 

            let touchX, touchY;
            const canvasRect = canvas.getBoundingClientRect();
            const touch = e.originalEvent.touches[0];

            // 检查iframeElement是否存在
            if (iframeElement) {
                // 获取iframe和触摸位置
                const iframeRect = iframeElement.getBoundingClientRect();

                // 计算触摸在iframe内的坐标
                touchX = touch.clientX - iframeRect.left;
                touchY = touch.clientY - iframeRect.top;
            } else {
                // 如果不在iframe中，直接使用触摸坐标
                touchX = touch.clientX;
                touchY = touch.clientY;
            }

            // 计算相对于canvas的位置
            const canvasX = touchX - canvasRect.left;
            const canvasY = touchY - canvasRect.top;

            const dx = canvasX - dragStartX;
            const dy = canvasY - dragStartY;

            offsetX = initialOffsetX + dx / scale;
            offsetY = initialOffsetY + dy / scale;

            drawMap();
        });

        $(parentDoc).on('touchend.mapTouch touchcancel.mapTouch', function(e) {
            // 处理触摸结束时的稳定性
            if (touchZoomActive) {
                // 如果手指离开，确保地图不会跳跃
                touchZoomActive = false;
                lastTouchDistance = 0;

                // 防止触摸结束后的地图跳跃
                setTimeout(() => {
                    // 确保缩放稳定
                    drawMap();
                }, 50);
            }

            // 结束单指拖拽
            if (isDragging && (!e.originalEvent.touches || e.originalEvent.touches.length === 0)) {
                isDragging = false;
            }
        });

        // 设置拖拽事件
        $(monitorScreen).on('mousedown', function(e) {
            // 只处理左键点击
            if (e.button !== 0) return;
            // 忽略按钮上的点击
            if ($(e.target).is('button')) return;

            e.preventDefault();

            isDragging = true;
            const rect = canvas.getBoundingClientRect();
            dragStartX = e.clientX - rect.left;
            dragStartY = e.clientY - rect.top;
            initialOffsetX = offsetX;
            initialOffsetY = offsetY;

            $(this).addClass('grabbing');
        });

        // 修改为使用window.parent.document以处理iframe环境
        $(window.parent.document).on('mousemove.mapDrag', function(e) {
            if (!isDragging) return;

            let canvasX, canvasY;
            const canvasRect = canvas.getBoundingClientRect();

            // 检查iframeElement是否存在
            if (iframeElement) {
                // 获取鼠标相对于iframe的位置
                const iframeRect = iframeElement.getBoundingClientRect();

                // 计算鼠标在iframe内的坐标
                const mouseX = e.clientX - iframeRect.left;
                const mouseY = e.clientY - iframeRect.top;

                // 计算相对于canvas的位置
                canvasX = mouseX - canvasRect.left;
                canvasY = mouseY - canvasRect.top;
            } else {
                // 如果不在iframe中，直接使用相对于canvas的坐标
                canvasX = e.clientX - canvasRect.left;
                canvasY = e.clientY - canvasRect.top;
            }

            const dx = canvasX - dragStartX;
            const dy = canvasY - dragStartY;

            offsetX = initialOffsetX + dx / scale;
            offsetY = initialOffsetY + dy / scale;

            drawMap();
        });

        // 修改为使用window.parent.document以处理iframe环境
        $(window.parent.document).on('mouseup.mapDrag', function() {
            if (isDragging) {
                isDragging = false;
                $(monitorScreen).removeClass('grabbing');
            }
        });

        // 设置滚轮缩放
        $(monitorScreen).on('wheel', function(e) {
            e.preventDefault();

            const rect = canvas.getBoundingClientRect();
            const mouseX = e.originalEvent.clientX - rect.left;
            const mouseY = e.originalEvent.clientY - rect.top;

            const delta = -Math.sign(e.originalEvent.deltaY);
            const oldScale = scale;
            const newScale = Math.max(minScale, Math.min(maxScale, oldScale * (1 + delta * zoomSensitivity)));

            if (oldScale === newScale) return;

            const dpr = window.devicePixelRatio || 1;
            const cw = canvas.width / dpr;
            const ch = canvas.height / dpr;

            const worldBeforeX = (mouseX - cw / 2) / oldScale - offsetX;
            const worldBeforeY = (mouseY - ch / 2) / oldScale - offsetY;

            scale = newScale;

            offsetX = -worldBeforeX + mouseX / scale - cw / (2 * scale);
            offsetY = -worldBeforeY + mouseY / scale - ch / (2 * scale);

            drawMap();
        });

        // 监听画布大小变化
        const resizeObserver = new ResizeObserver(resizeCanvas);
        resizeObserver.observe(canvas);

        // 初始化
        resetView();
        resizeCanvas();

        // 更新清理函数以移除触摸事件监听器
        $container.on('remove', function() {
            // 移除jQuery事件监听器
            $(monitorScreen).off('mousedown wheel');
            $(window.parent.document).off('mousemove.mapDrag mouseup.mapDrag');
            $(window.parent.document).off('touchmove.mapTouch touchend.mapTouch touchcancel.mapTouch');

            // 停止动画
            if (animationFrameId) {
                cancelAnimationFrame(animationFrameId);
                animationFrameId = null;
            }

            // 断开ResizeObserver
            if (resizeObserver) {
                resizeObserver.disconnect();
            }
        });
    }
})(render || (render = {}));

// 地图数据解析函数
function parseMapData(text) {
    const lines = text.split('\n');
    const mapData = { 
        nodes: [], 
        edges: [], 
        metadata: {},
        currentLocation: null,
        free_movement: "A" // 默认为A(临近模式)
    };

    const nodeIds = new Set();
    let lineNumber = 0;

    for (const line of lines) {
        lineNumber++;
        const trimmedLine = line.trim();
        if (trimmedLine === '' || trimmedLine.startsWith('#')) continue;

        let match;
        const nodeRegex = /^n:([^|]+)\|([^|]+)\|([^|@]+)(?:@([^|]*))?(?:\|)(\d+)\|(\d+)\|([rc])\|(.*)$/i;
        if ((match = trimmedLine.match(nodeRegex))) {
            const [, id, type, labelRaw, charactersStr, xStr, yStr, shapeCode, dimensions] = match;

            const label = labelRaw.trim();
            const x = parseInt(xStr, 10);
            const y = parseInt(yStr, 10);

            if (isNaN(x) || isNaN(y)) {
                continue;
            }

            const node = { 
                id: id.trim(), 
                type: type.trim(), 
                label, 
                x, 
                y, 
                shape: shapeCode.toLowerCase().trim(),
                characters: charactersStr ? charactersStr.split(',').map(c => c.trim()).filter(c => c) : []
            };

            if (
                (charactersStr !== undefined && (
                    charactersStr === "" || 
                    (charactersStr.split(',').map(c => c.trim()).some(c => c === '' || c === 'current'))
                ))
            ) {
                mapData.currentLocation = node.id;
            }

            if (nodeIds.has(node.id)) {
                continue;
            }

            nodeIds.add(node.id);

            if (node.shape === 'r') {
                const sizeMatch = dimensions.match(/^(\d+)x(\d+)$/i);
                if (sizeMatch) {
                    node.width = parseInt(sizeMatch[1], 10);
                    node.height = parseInt(sizeMatch[2], 10);
                } else {
                    node.width = 40;
                    node.height = 30;
                }
            } else if (node.shape === 'c') {
                const radius = parseInt(dimensions, 10);
                node.radius = !isNaN(radius) ? radius : 15;
            }

            mapData.nodes.push(node);
        }
        else if ((match = trimmedLine.match(/^e:([^|]+)\|([^|]+)(?:\|([^|]+))?$/i))) {
            const [, from, to, typeCode] = match;
            const edge = { 
                from: from.trim(), 
                to: to.trim(), 
                type: typeCode ? typeCode.trim() : 'c'
            };

            mapData.edges.push(edge);
        }
        else if ((match = trimmedLine.match(/^m:([^=]+)=(.*)$/i))) {
            const [, key, value] = match;
            const trimmedKey = key.trim().toLowerCase();

            if (trimmedKey === 'fm') {
                const moveVal = value.trim().toUpperCase();

                if (moveVal === "F" || moveVal === "A" || moveVal === "N") {
                    mapData.free_movement = moveVal;
                } else {
                    mapData.free_movement = "A";
                }
            } else if (trimmedKey === 'desc') {
                mapData.metadata['description'] = value.trim();
            } else {
                mapData.metadata[trimmedKey] = value.trim();
            }
        }
    }

    const nodesById = new Map(mapData.nodes.map(node => [node.id, node]));
    for (let i = 0; i < mapData.edges.length; i++) {
        const edge = mapData.edges[i];
        const fromNode = nodesById.get(edge.from);
        const toNode = nodesById.get(edge.to);

        if (!fromNode || !toNode) {
            continue;
        }
    }

    return mapData;
}

// 获取线段与形状的交点
function getIntersectionPointWithShape(fromX, fromY, targetNode) {
    const targetX = targetNode.x;
    const targetY = targetNode.y;
    const dx = targetX - fromX;
    const dy = targetY - fromY;
    const dist = Math.sqrt(dx*dx + dy*dy);

    if (dist < 0.1) return { x: targetX, y: targetY };

    const normDx = dx / dist;
    const normDy = dy / dist;

    if (targetNode.shape === 'r') {
        const w = targetNode.width || 40;
        const h = targetNode.height || 30;
        const hw = w/2;
        const hh = h/2;
        let t = Infinity;

        if (Math.abs(dx) > 1e-6) {
            let t1 = (targetX-hw-fromX)/dx;
            let y1 = fromY+t1*dy;
            if(t1>1e-6 && Math.abs(y1-targetY)<=hh + 1e-6) t = Math.min(t, t1);

            let t2 = (targetX+hw-fromX)/dx;
            let y2 = fromY+t2*dy;
            if(t2>1e-6 && Math.abs(y2-targetY)<=hh + 1e-6) t = Math.min(t, t2);
        }

        if (Math.abs(dy) > 1e-6) {
            let t3 = (targetY-hh-fromY)/dy;
            let x3 = fromX+t3*dx;
            if(t3>1e-6 && Math.abs(x3-targetX)<=hw + 1e-6) t = Math.min(t, t3);

            let t4 = (targetY+hh-fromY)/dy;
            let x4 = fromX+t4*dx;
            if(t4>1e-6 && Math.abs(x4-targetX)<=hw + 1e-6) t = Math.min(t, t4);
        }

        if (t < Infinity) return { x: fromX + t * dx, y: fromY + t * dy };
    } else if (targetNode.shape === 'c') {
        const r = targetNode.radius || 15;
        return { x: targetX - normDx * r, y: targetY - normDy * r };
    }

    return { x: targetX, y: targetY };
}

// 错误处理包装函数
function errorCatched(fn) {
    return (...args) => {
        try {
            const result = fn(...args);
            if (result instanceof Promise) {
                return result.catch(error => {
                    console.error('错误:', error);
                });
            }
            return result;
        } catch (error) {
            console.error('错误:', error);
            return undefined;
        }
    };
}

// 清理不在最近N个楼层的地图UI
async function cleanupOldMapUI() {
    try {
        // 如果RENDER_DEPTH设为0，表示渲染所有消息，不需要清理
        if (RENDER_DEPTH === 0) return;

        // 直接使用getChatMessages获取所有消息
        const allMessages = await getChatMessages("0-{{lastMessageId}}");

        // 筛选出非用户和非系统的消息
        const filteredMessages = allMessages.filter(msg => 
            msg.role === 'assistant' && !msg.is_hidden);

        // 筛选出包含地图标记的消息
        const mapMessages = [];
        for (const msg of filteredMessages) {
            if (msg.message) {
                // 使用标准正则和宽松正则尝试匹配
                const standardMatch = msg.message.match(map_regex);
                const looseMatch = !standardMatch ? msg.message.match(loose_map_regex) : null;

                if (standardMatch || looseMatch) {
                    mapMessages.push(msg);
                }
            }
        }

        // 如果没有地图消息，直接返回
        if (mapMessages.length === 0) return;

        // 获取最后一条消息的ID
        const lastMessageId = getLastMessageId();

        // 按ID排序
        mapMessages.sort((a, b) => a.message_id - b.message_id);

        // 清理不符合渲染深度条件的地图UI
        for (const msg of mapMessages) {
            // 检查消息是否在渲染深度范围内
            if (lastMessageId - msg.message_id >= RENDER_DEPTH) {
                const $mes_text = retrieveDisplayedMessage(msg.message_id);
                if ($mes_text && $mes_text.length > 0) {
                    $mes_text.find('.map_graph').remove();
                }
            }
        }
    } catch (error) {
        console.error("清理旧地图UI时出错:", error);
    }
}

// 渲染所有消息（仅限最近N个）
async function renderAllMessage() {
    try {
        // 直接使用getChatMessages获取所有消息
        const allMessages = await getChatMessages("0-{{lastMessageId}}");

        // 筛选出非用户和非系统的消息
        const filteredMessages = allMessages.filter(msg => 
            msg.role === 'assistant' && !msg.is_hidden);

        // 筛选出包含地图标记的消息
        const mapMessages = [];
        for (const msg of filteredMessages) {
            if (msg.message) {
                // 使用标准正则和宽松正则尝试匹配
                const standardMatch = msg.message.match(map_regex);
                const looseMatch = !standardMatch ? msg.message.match(loose_map_regex) : null;

                if (standardMatch || looseMatch) {
                    mapMessages.push(msg);
                }
            }
        }

        // 如果没有地图消息，直接返回
        if (mapMessages.length === 0) return;

        // 获取最后一条消息的ID
        const lastMessageId = getLastMessageId();

        // 按ID排序
        mapMessages.sort((a, b) => a.message_id - b.message_id);

        // 确定要渲染的消息
        const messagesToRender = [];

        for (const msg of mapMessages) {
            // 根据RENDER_DEPTH判断是否需要渲染
            // 如果RENDER_DEPTH为0，则渲染所有消息
            // 否则只渲染在深度范围内的消息
            if (RENDER_DEPTH === 0 || (lastMessageId - msg.message_id < RENDER_DEPTH)) {
                messagesToRender.push(msg);
            }
        }

        // 渲染选中的消息
        for (const msg of messagesToRender) {
            await renderOneMessage(msg.message_id);
        }
    } catch (error) {
        console.error("渲染地图时出错:", error);
    }
}

// 单条消息渲染函数
async function renderOneMessage(message_id) {
    try {
        // 检查深度限制
        const lastMessageId = getLastMessageId();
        // 如果设置了深度限制且消息不在范围内，直接返回
        if (RENDER_DEPTH !== 0 && (lastMessageId - message_id >= RENDER_DEPTH)) {
            return;
        }

        // 确保message_id是数字类型
        const messageIdNum = parseInt(message_id, 10);

        // 获取消息内容并检查是否包含地图标记
        const messages = await getChatMessages(messageIdNum);
        if (!messages || messages.length === 0 || !messages[0]?.message) {
            return;
        }

        const message = messages[0].message;

        // 尝试标准正则和宽松正则两种匹配方式
        let match = message.match(map_regex);
        if (!match) {
            match = message.match(loose_map_regex);
        }

        if (!match) {
            return;
        }

        const mapContent = match[1];

        // 增加重试逻辑
        let $mes_text = null;
        let retryCount = 0;
        const maxRetries = 3;

        while (retryCount < maxRetries) {
            $mes_text = retrieveDisplayedMessage(messageIdNum);

            // 确保消息DOM元素存在
            if ($mes_text && $mes_text.length > 0) {
                break;
            }

            // 等待一段时间再重试
            await new Promise(resolve => setTimeout(resolve, 200));
            retryCount++;
        }

        // 最终检查
        if (!$mes_text || $mes_text.length === 0) {
            return;
        }

        // 删除现有的地图UI
        const existingMaps = $mes_text.find('.map_graph');
        if (existingMaps.length > 0) {
            existingMaps.remove();
        }

        // 应用延迟设置
        const renderDelay = option.option?.init_delay || 0;
        if (renderDelay > 0) {
            setTimeout(() => {
                try {
                    const $newMap = render.extract_map_element(mapContent);
                    $mes_text.append($newMap);
                } catch (innerError) {
                    console.error(`延迟渲染消息 ${messageIdNum} 的地图时出错:`, innerError);
                }
            }, renderDelay);
        } else {
            try {
                $mes_text.append(render.extract_map_element(mapContent));
            } catch (appendError) {
                console.error(`渲染并添加消息 ${messageIdNum} 的地图时出错:`, appendError);

                // 尝试简单追加到消息元素
                try {
                    const simpleMap = render.extract_map_element(mapContent);
                    $('.mes[mesid="' + messageIdNum + '"]', window.parent.document).append(simpleMap);
                } catch (fallbackError) {
                    console.error(`替代渲染方法也失败:`, fallbackError);
                }
            }
        }
    } catch (error) {
        console.error(`渲染消息 ${message_id} 的地图时出错:`, error);
    }
}

// 清理超出渲染深度的地图UI
function cleanupOldMapUIs() {
    if (RENDER_DEPTH === 0) return; 

    const lastMessageId = getLastMessageId();
    $('#chat .mes').each(function() {
        const messageId = parseInt($(this).attr('mesid'));
        if (!isNaN(messageId) && (lastMessageId - messageId >= RENDER_DEPTH)) {
            // 找到超出深度范围的消息中的地图UI并删除
            $(this).find('.map_graph').remove();
        }
    });
}

// 初始化程序
$(async () => {
    try {
        // 首先确保加载设置
        await errorCatched(async () => {
            await option.update();
            if (!option.option) {
                option.option = {
                    should_send_directly: true,
                    init_delay: 0,
                    travel_mode: false
                };
            }
        })();

        await errorCatched(render.update)();

        // 初始化时渲染所有符合条件的消息
        await errorCatched(renderAllMessage)();

        // 监听消息事件
        eventOn(tavern_events.CHARACTER_MESSAGE_RENDERED, errorCatched(async (message_id) => {
            setTimeout(async () => {
                try {
                    const messages = await getChatMessages(message_id);
                    if (!messages || messages.length === 0) {
                        return;
                    }

                    const message = messages[0]?.message || '';

                    if (message.length > 0) {
                        // 使用标准正则和宽松正则都尝试匹配
                        const standardMatch = message.match(map_regex);
                        const looseMatch = message.match(loose_map_regex);

                        if (standardMatch || looseMatch) {
                            // 再增加一个延迟以确保DOM完全加载
                            setTimeout(() => {
                                renderOneMessage(message_id);
                                setTimeout(() => {
                                    renderOneMessage(message_id);
                                    cleanupOldMapUIs();
                                }, 1000);
                            }, 1000);
                        }
                    }
                } catch (error) {
                    console.error(`处理消息 ${message_id} 时出错:`, error);
                }
            }, 1500); // 增加延迟到1500ms，给DOM加载更多时间
        }));

        eventOn(tavern_events.MESSAGE_UPDATED, errorCatched(async (message_id) => {
            await renderOneMessage(message_id);
            cleanupOldMapUIs();
        }));

        eventOn(tavern_events.MESSAGE_SWIPED, errorCatched(async (message_id) => {
            await renderOneMessage(message_id);
            cleanupOldMapUIs();
        }));

        eventOn(tavern_events.MESSAGE_DELETED, errorCatched(async () => {
            await renderAllMessage();
            cleanupOldMapUIs();
        }));

        // 监听聊天内容变化事件
        eventOn(tavern_events.CHAT_CHANGED, errorCatched(async () => {
            await errorCatched(renderAllMessage)();
            cleanupOldMapUIs();
        }));

        // 监听页面加载完成事件
        eventOn(tavern_events.CHAT_LOADED, errorCatched(async () => {
            // 给页面足够的时间加载DOM
            setTimeout(() => {
                errorCatched(renderAllMessage)();
                cleanupOldMapUIs();
            }, 1000);
        }));

        // 添加MutationObserver监听聊天区域的变化，捕获新添加的消息
        try {
            // 尝试获取聊天容器
            let chatContainer = document.getElementById('chat');
            if (!chatContainer) {
                chatContainer = document.querySelector('.chat_container');
            }
            if (!chatContainer) {
                chatContainer = document.querySelector('[id^="chat_"]');
            }
            if (!chatContainer) {
                chatContainer = document.body; 
            }

            // 在父窗口中找到聊天容器
            const parentChatContainer = window.parent.document.querySelector('#chat') || 
                                       window.parent.document.querySelector('.chat_container') || 
                                       window.parent.document.querySelector('[id^="chat_"]') ||
                                       window.parent.document.body;

            // 创建消息处理队列，避免重复处理
            const processedMessages = new Set();
            let processingBatch = false;

            // 创建消息队列，用于批量处理
            const messageQueue = [];

            // 批量处理消息的函数
            const processBatch = async () => {
                if (processingBatch || messageQueue.length === 0) return;

                processingBatch = true;

                // 复制队列并清空原队列
                const currentBatch = [...messageQueue];
                messageQueue.length = 0;

                try {
                    for (const mesId of currentBatch) {
                        if (!processedMessages.has(mesId)) {
                            processedMessages.add(mesId);
                            await renderOneMessage(mesId);

                            setTimeout(() => {
                                renderOneMessage(mesId);
                            }, 2000);
                        }
                    }
                } catch (err) {
                    console.error("批量处理消息时出错:", err);
                } finally {
                    processingBatch = false;

                    // 如果在处理过程中有新消息加入队列，继续处理
                    if (messageQueue.length > 0) {
                        setTimeout(processBatch, 500);
                    }
                }
            };

            // 创建观察器
            const observer = new MutationObserver((mutations) => {
                // 检查是否有新消息添加
                let newMessagesFound = false;

                mutations.forEach(mutation => {
                    if (mutation.type === 'childList' && mutation.addedNodes.length > 0) {
                        mutation.addedNodes.forEach(node => {
                            // 检查是否是消息元素
                            if (node.nodeType === Node.ELEMENT_NODE) {
                                if (node.classList && node.classList.contains('mes')) {
                                    const mesId = node.getAttribute('mesid');
                                    if (mesId && !processedMessages.has(mesId)) {
                                        newMessagesFound = true;
                                        messageQueue.push(mesId);
                                    }
                                } else {
                                    // 检查子元素中是否有消息
                                    const messages = node.querySelectorAll('.mes');
                                    if (messages.length > 0) {
                                        newMessagesFound = true;
                                        messages.forEach(mes => {
                                            const mesId = mes.getAttribute('mesid');
                                            if (mesId && !processedMessages.has(mesId)) {
                                                messageQueue.push(mesId);
                                            }
                                        });
                                    }
                                }
                            }
                        });
                    }
                });

                // 如果找到新消息，进行处理
                if (newMessagesFound && !processingBatch) {
                    setTimeout(processBatch, 1000); 
                }
            });

            // 配置观察器
            const observerConfig = {
                childList: true,  
                subtree: true     
            };

            // 开始观察
            observer.observe(parentChatContainer, observerConfig);
        } catch (obsError) {
            console.error("设置MutationObserver时出错:", obsError);
        }

        // 监听Lorebook更新事件
        eventOn(tavern_events.WORLDINFO_UPDATED, errorCatched(async (lorebook) => {
            if (lorebook !== lorebook_name) {
                return;
            }

            const optionChanged = await option.update();
            const styleChanged = await render.update();

            if (optionChanged || styleChanged) {
                await renderAllMessage();
            }
        }));
    } catch (error) {
        console.error("地图脚本初始化失败:", error);
    }
});

