  // 添加渲染角色关系参数（RP）条目的函数
  function renderRelationshipParameterItem(item) {
    const { key, values } = item;

    // 解析关系参数值
    let currentValue = 0;
    let maxValue = 100;
    let changeValue = '±0';
    let reason = '无';
    let stage = '';

    // 尝试解析第一个值（当前值/最大值）
    if (values[0] && values[0].includes('@')) {
      const [current, max] = values[0];
      currentValue = parseInt(current) || 0;
      maxValue = parseInt(max) || 100;
    } else if (values[0]) {
      currentValue = parseInt(values[0]) || 0;
    }

    // 获取变化值
    if (values[1]) {
      changeValue = values[1];
    }

    // 获取变化原因
    if (values[2]) {
      reason = values[2];
    }

    // 获取阶段描述
    if (values[3]) {
      stage = values[3];
    }

    // 统一使用莫兰迪蓝色
    const color = '#7797b9';
    let icon = '<i class="fa-solid fa-circle-info fa-fw"></i>';

    // 根据不同的关系参数类型设置不同图标
    switch (key) {
      case '好感度':
        icon = '<i class="fa-solid fa-heart fa-fw"></i>';
        break;
      case '信任度':
        icon = '<i class="fa-solid fa-handshake fa-fw"></i>';
        break;
      case '堕落度':
        icon = '<i class="fa-solid fa-mask fa-fw"></i>';
        break;
      case '支配度':
        icon = '<i class="fa-solid fa-crown fa-fw"></i>';
        break;
      case '依赖度':
        icon = '<i class="fa-solid fa-link fa-fw"></i>';
        break;
      case '羞耻度':
        icon = '<i class="fa-solid fa-face-flushed fa-fw"></i>';
        break;
      case '服从度':
        icon = '<i class="fa-solid fa-gavel fa-fw"></i>';
        break;
      case '声誉':
        icon = '<i class="fa-solid fa-star fa-fw"></i>';
        break;
      default:
        icon = '<i class="fa-solid fa-sliders fa-fw"></i>';
    }

    // 变化显示
    let changeDisplay = '';
    if (changeValue && changeValue !== '±0') {
      const isPositive = changeValue.startsWith('+');
      changeDisplay = `<span class="status-change ${isPositive ? 'positive' : 'negative'}">${changeValue}</span>`;
    }

    // 原因显示
    let reasonDisplay = '';
    if (reason && reason !== '无') {
      reasonDisplay = `<span class="status-reason">${reason}</span>`;
    }

    // 阶段显示
    let stageDisplay = '';
    if (stage && stage.trim() !== '') {
      stageDisplay = `<span class="relation-stage">${stage}</span>`;
    }

    return `<div class="status-item relation-parameter" data-parameter-type="${key}">
              <div class="status-header">
                <span class="status-label">${icon} ${key}</span>
                ${stageDisplay}
              </div>
              ${createProgressBar(currentValue, maxValue, color)}
              <div class="status-footer">
                <div class="status-details-row">
                  <span class="status-value">${currentValue}/${maxValue}</span>
                  ${changeDisplay} ${reasonDisplay}
                </div>
              </div>
            </div>`;
  }

  // 根据分类选择渲染函数
  function renderItemByCategory(category, item) {
    switch (category) {
      case 'ST':
        return renderSceneTimeItem(item);
      case 'CP':
        return renderCharacterProfileItem(item);
      case 'CR':
        return renderResourcesInventoryItem(item);
      case 'CV':
        return renderVitalsGaugesItem(item);
      case 'CS':
        return renderConditionDescriptorsItem(item);
      case 'RP': // 添加RP渲染函数
        return renderRelationshipParameterItem(item);
      case 'AE':
        return renderActivityCountersItem(item);
      case 'WP':
        return renderWorldPlotItem(item);
      case 'MI':
        return renderMetaInfoItem(item);
      default:
        // 通用渲染
        return `<div class="status-item">
                  <div class="status-header">
                    <span class="status-label">${item.key}</span>
                  </div>
                  <div class="status-value">${item.values.flat().join(' ')}</div>
                </div>`;
    }
  }

  // 创建节标题
  function createSectionTitle(category) {
    const mapping = CATEGORY_MAPPING[category] || { name: category, icon: 'fa-circle-info' };
    return `<div class="status-section-title" data-category="${category}">
              <div class="status-section-toggle">
                <i class="fa-solid fa-chevron-down collapse-icon"></i>
              </div>
              <i class="fa-solid ${mapping.icon} fa-fw status-section-icon"></i> ${mapping.name}
            </div>`;
  }

  // 加载样式
  async function extract_style() {
    // 这里可以从Lorebook中加载自定义样式
    // 现在先使用默认样式
    return default_style;
  }

  async function update() {
    const old_style = style;
    style = await extract_style();
    return old_style !== style;
  }
  render_1.update = update;

  // 提取状态栏元素
  function extract_statusbar_element(text) {
    try {
      // 解析状态栏文本
      const parsedData = parseStatusBarText(text);
      if (isEmpty(parsedData.categories)) {
        console.log('[状态栏] 解析结果为空，不渲染状态栏');
        return null;
      }

      // 创建主容器
      const $div = $('<div class="status-bar-render-container">');

      // 添加样式
      if (style) {
        $div.append(`<style>${style}</style>`);
      }

      // 创建状态栏容器
      const container = document.createElement('div');
      container.className = 'status-bar-container';

      // 首先处理场景与时间 (ST)，显示在顶部
      if (parsedData.categories['ST']) {
        const topInfoRow = document.createElement('div');
        topInfoRow.className = 'status-top-info';

        parsedData.categories['ST'].forEach(item => {
          if (['时间', '当前地点', '天气'].includes(item.key)) {
            topInfoRow.innerHTML += renderItemByCategory('ST', item);
          }
        });

        if (topInfoRow.innerHTML.trim() !== '') {
          container.appendChild(topInfoRow);
        }
      }

      // 按顺序处理其他分类，添加RP到categoryOrder中
      const categoryOrder = ['CP', 'CR', 'CV', 'RP', 'CS', 'AE', 'WP', 'MI'];

      // 处理剩余分类
      categoryOrder.forEach(category => {
        if (parsedData.categories[category] && parsedData.categories[category].length > 0) {
          const section = document.createElement('div');
          section.className = `status-section ${category.toLowerCase()}-section`;
          section.innerHTML = createSectionTitle(category);

          const contentDiv = document.createElement('div');
          contentDiv.className = 'status-section-content';
          if (!option.option.default_expanded) {
            contentDiv.classList.add('collapsed');
          }

          parsedData.categories[category].forEach(item => {
            contentDiv.innerHTML += renderItemByCategory(category, item);
          });

          section.appendChild(contentDiv);
          container.appendChild(section);
        }
      });

      // 处理未知分类
      const knownCategories = ['ST', ...categoryOrder];
      Object.keys(parsedData.categories).forEach(category => {
        if (!knownCategories.includes(category) && parsedData.categories[category].length > 0) {
          const section = document.createElement('div');
          section.className = `status-section unknown-section`;
          section.innerHTML = `<div class="status-section-title" data-category="${category}">
                                <div class="status-section-toggle">
                                  <i class="fa-solid fa-chevron-down collapse-icon"></i>
                                </div>
                                <i class="fa-solid fa-question fa-fw status-section-icon"></i> ${category}
                               </div>`;

          const contentDiv = document.createElement('div');
          contentDiv.className = 'status-section-content';
          if (!option.option.default_expanded) {
            contentDiv.classList.add('collapsed');
          }

          parsedData.categories[category].forEach(item => {
            // 使用通用渲染
            contentDiv.innerHTML += `<div class="status-item">
                                    <div class="status-header">
                                      <span class="status-label">${item.key}</span>
                                    </div>
                                    <div class="status-value">${item.values.flat().join(' ')}</div>
                                  </div>`;
          });

          section.appendChild(contentDiv);
          container.appendChild(section);
        }
      });

      $div.append(container);
      return $div[0];
    } catch (error) {
      console.error('[状态栏] 渲染状态栏时发生错误:', error);
      return null;
    }
  }
  render_1.extract_statusbar_element = extract_statusbar_element;
})(render || (render = {}));

// 渲染单条消息
async function renderOneMessage(message_id) {
  try {
    const message = (await getChatMessages(message_id))[0].message;
    const match = message.match(status_regex);
    if (!match) {
      return;
    }

    const statusBarContent = match[1];

    // 解析状态栏数据
    const parsedData = parseStatusBarText(statusBarContent);

    // 如果解析到了数据，保存到聊天变量
    if (parsedData && Object.keys(parsedData.categories).length > 0) {
      // 异步保存数据，不阻塞渲染流程
      saveCharacterDataToVariables(parsedData).catch(err => {
        console.error('[状态栏] 保存角色数据时出错:', err);
      });
    }

    const $statusbar_element = render.extract_statusbar_element(statusBarContent);
    const $mes_text = retrieveDisplayedMessage(message_id);

    // 删除现有的状态栏
    $mes_text.find('.status-bar-render-container').remove();

    // 添加新的状态栏
    if ($statusbar_element) {
      $mes_text.append($statusbar_element);

      // 添加事件处理，确保传入jQuery对象
      attachEventHandlers($($statusbar_element));
    }
  } catch (error) {
    console.error('[状态栏] 渲染消息时出错:', error);
  }
}

// 渲染所有消息
async function renderAllMessages() {
  $('#chat', window.parent.document)
    .children(".mes[is_user='false'][is_system='false']")
    .each((_index, node) => {
      renderOneMessage(Number(node.getAttribute('mesid')));
    });
}

// 附加事件处理器
function attachEventHandlers($element) {
  // 为剧情选项添加点击事件
  $element.find('.plot-option-item, .world-item-entry').on('click', function () {
    if ($(this).closest('.interactive-list').length > 0) {
      const optionText = $(this).text().trim();
      console.log('[状态栏] 选择选项:', optionText);

      // WP可触发设置下，将内容直接填入发送框
      if (option.option?.wp_triggerable && $(this).closest('.world-item').length > 0) {
        console.log('[状态栏] 触发WP条目:', optionText);
        triggerSlash(`/setinput ${optionText}`);
        return;
      }

      // 原有的逻辑
      triggerSlash(`/setinput ${optionText}`);
    }
  });

  // 为地点选项添加点击事件
  $element.find('.status-locations li').on('click', function () {
    const locationText = $(this).text().trim();
    console.log('[状态栏] 选择地点:', locationText);
    triggerSlash(`/setinput {{User}}决定前往: ${locationText}`);
  });

  // 为新闻和周边事件添加点击事件
  $element.find('.status-news li, .status-nearby-events li').on('click', function () {
    const newsText = $(this).text().trim();
    console.log('[状态栏] 关注事件:', newsText);

    // WP可触发设置下，简化为直接填入文本
    if (option.option?.wp_triggerable) {
      triggerSlash(`/setinput ${newsText}`);
    } else {
      triggerSlash(`/setinput {{User}}对"${newsText}"表示关注`);
    }
  });

  // 为折叠按钮添加点击事件
  $element.find('.status-section-title').on('click', function () {
    const $content = $(this).siblings('.status-section-content');
    $content.toggleClass('collapsed');

    // 更新折叠图标
    const $icon = $(this).find('.collapse-icon');
    if ($content.hasClass('collapsed')) {
      $icon.removeClass('fa-chevron-down').addClass('fa-chevron-right');
    } else {
      $icon.removeClass('fa-chevron-right').addClass('fa-chevron-down');
    }
  });

  // 初始化折叠图标状态
  $element.find('.status-section-content').each(function () {
    const $content = $(this);
    const $icon = $content.siblings('.status-section-title').find('.collapse-icon');

    if ($content.hasClass('collapsed')) {
      $icon.removeClass('fa-chevron-down').addClass('fa-chevron-right');
    } else {
      $icon.removeClass('fa-chevron-right').addClass('fa-chevron-down');
    }
  });
}

// 错误捕获包装器
function errorCatched(fn) {
  return (...args) => {
    try {
      const result = fn(...args);
      if (result instanceof Promise) {
        return result.catch(error => {
          console.error(`[状态栏] 错误:`, error);
          triggerSlash(`/echo severity=error [状态栏]${error.stack ? error.stack : error.name + ': ' + error.message}`);
        });
      }
      return result;
    } catch (error) {
      console.error(`[状态栏] 错误:`, error);
      triggerSlash(`/echo severity=error [状态栏]${error.stack ? error.stack : error.name + ': ' + error.message}`);
      return undefined;
    }
  };
}

// 创建菜单按钮函数
function createMenuButton() {
  try {
    const parentDoc = window.parent.document;

    // 检查按钮是否已存在
    let $button = $(`#${BUTTON_ID}`, parentDoc);

    // 如果按钮存在但不在扩展菜单中，删除它
    if ($button.length > 0 && !$button.closest('#extensionsMenu').length) {
      console.log('[状态栏] 移除旧按钮实例');
      $button.remove();
      $button = $(`#${BUTTON_ID}`, parentDoc);
    }

    // 如果按钮不存在，创建并插入
    if ($button.length === 0) {
      // 添加样式
      addButtonStyles();

      const buttonHtml = `
        <div id="${BUTTON_ID}" class="list-group-item flex-container flexGap5 interactable" 
             title="${BUTTON_TOOLTIP}" tabIndex="0">
          <i class="${BUTTON_ICON}"></i>
          <span>${BUTTON_TEXT}</span>
        </div>
      `;

      const $extensionsMenu = $('#extensionsMenu', parentDoc);

      if ($extensionsMenu.length) {
        try {
          $extensionsMenu.append(buttonHtml);
          console.log('[状态栏] 按钮已插入扩展菜单');

          // 绑定点击事件
          $(parentDoc)
            .off(`click.${BUTTON_ID}`)
            .on(`click.${BUTTON_ID}`, `#${BUTTON_ID}`, event => {
              event.preventDefault();
              showStatusBarManager();
            });
        } catch (e) {
          console.error('[状态栏] 无法将按钮插入扩展菜单', e);
        }
      } else {
        console.warn('[状态栏] 未找到扩展菜单 (#extensionsMenu)');
      }
    }
  } catch (error) {
    console.error('[状态栏] 创建菜单按钮时出错:', error);
  }
}

