// 获取保存的预设
function getSavedPresets() {
  try {
    const savedPresets = localStorage.getItem(PRESET_STORAGE_KEY);
    // 确保返回的是数组
    const presets = savedPresets ? JSON.parse(savedPresets) : [];

    // 验证格式
    if (!Array.isArray(presets)) {
      console.error('[状态栏] 预设数据格式错误，重置为空数组');
      return [];
    }

    return presets;
  } catch (error) {
    console.error('[状态栏] 获取配置失败:', error);
    return [];
  }
}

// 保存预设
function savePreset(name, enabledEntries) {
  try {
    const presets = getSavedPresets();

    // 确保enabledEntries中的UID是数字类型
    const normalizedEntries = enabledEntries.map(uid => parseInt(uid));
    console.log('[状态栏] 保存配置:', name, '包含启用条目:', normalizedEntries);

    // 检查是否已经存在同名配置
    const existingIndex = presets.findIndex(p => p.name === name);

    if (existingIndex !== -1) {
      // 更新现有配置
      presets[existingIndex] = { name, enabledEntries: normalizedEntries, timestamp: Date.now() };
    } else {
      // 添加新配置，但确保不超过最大数量
      if (presets.length >= MAX_PRESETS) {
        alert(`已达到配置数量上限 (${MAX_PRESETS})，请先删除一些配置。`);
        return false;
      }
      presets.push({ name, enabledEntries: normalizedEntries, timestamp: Date.now() });
    }

    localStorage.setItem(PRESET_STORAGE_KEY, JSON.stringify(presets));
    return true;
  } catch (error) {
    console.error('[状态栏] 保存配置失败:', error);
    return false;
  }
}

// 删除预设
function deletePreset(name) {
  try {
    const presets = getSavedPresets();

    // 日志记录原始预设数量
    console.log(`[状态栏] 删除前预设数量: ${presets.length}`);

    // 确保name是字符串并进行严格比较
    const exactName = String(name).trim();
    const newPresets = presets.filter(p => String(p.name).trim() !== exactName);

    // 日志记录过滤后预设数量
    console.log(`[状态栏] 删除后预设数量: ${newPresets.length}`);

    // 保存回本地存储
    localStorage.setItem(PRESET_STORAGE_KEY, JSON.stringify(newPresets));

    // 确认是否成功删除
    const checkPresets = getSavedPresets();
    const stillExists = checkPresets.some(p => String(p.name).trim() === exactName);
    console.log(`[状态栏] 验证删除: ${stillExists ? '失败' : '成功'}`);

    return !stillExists;
  } catch (error) {
    console.error('[状态栏] 删除配置失败:', error);
    return false;
  }
}

// 应用预设
async function applyPreset(preset) {
  try {
    console.log('[状态栏] 开始应用配置:', preset.name, '包含', preset.enabledEntries.length, '个启用的条目');

    // 获取所有条目
    const entries = await getLorebookEntries(lorebook_name);
    console.log('[状态栏] 获取到世界书条目总数:', entries.length);

    // 创建更新后的条目数组
    const updatedEntries = [];
    let modifiedCount = 0;

    entries.forEach(entry => {
      // 创建条目的副本
      const updatedEntry = { ...entry };

      // 忽略设置条目
      if (entry.comment.startsWith('设置-')) {
        updatedEntries.push(updatedEntry);
        return;
      }

      // 检查条目是否在预设中启用 (确保使用数字类型比较)
      const entryUid = parseInt(entry.uid);
      const shouldBeEnabled = preset.enabledEntries.includes(entryUid);

      // 只有在启用状态需要改变时才修改
      if (entry.enabled !== shouldBeEnabled) {
        updatedEntry.enabled = shouldBeEnabled;
        modifiedCount++;
        console.log(`[状态栏] 条目状态变更: "${entry.comment}" (UID: ${entry.uid}) ${entry.enabled ? '禁用' : '启用'}`);
      }

      updatedEntries.push(updatedEntry);
    });

    console.log(`[状态栏] 将更新 ${modifiedCount} 个条目的状态`);

    if (modifiedCount > 0) {
      // 更新世界书 - 确保每个条目都有uid
      await setLorebookEntries(lorebook_name, updatedEntries);
      console.log('[状态栏] 世界书更新成功');
    } else {
      console.log('[状态栏] 没有条目需要更新，预设可能已经应用');
    }

    return true;
  } catch (error) {
    console.error('[状态栏] 应用配置失败:', error);
    return false;
  }
}

// 显示状态栏管理器弹窗
async function showStatusBarManager() {
  try {
    // 获取状态栏世界书的所有条目
    const entries = await getLorebookEntries(lorebook_name);

    // 对条目进行排序处理
    entries.sort((a, b) => {
      // 首先检查是否设置条目，如果是，放到最后
      if (a.comment.startsWith('设置-') && !b.comment.startsWith('设置-')) return 1;
      if (!a.comment.startsWith('设置-') && b.comment.startsWith('设置-')) return -1;

      // 尝试使用display_index排序
      if (a.display_index !== undefined && b.display_index !== undefined) {
        return a.display_index - b.display_index;
      }

      // 如果没有display_index，则按注释文本排序
      return a.comment.localeCompare(b.comment, 'zh-CN');
    });

    // 按类别分组条目，忽略设置条目
    const entriesByCategory = entries
      .filter(entry => !entry.comment.startsWith('设置-'))
      .reduce((acc, entry) => {
        // 尝试从注释中提取类别，格式如: [ST|时间] 的 ST 部分
        let category = 'Other';
        const categoryMatch = entry.comment.match(/\[(\w+)\|/);
        if (categoryMatch) {
          category = categoryMatch[1];
        }

        if (!acc[category]) {
          acc[category] = [];
        }
        acc[category].push(entry);
        return acc;
      }, {});

    // 按类别优先级排序
    const categoryOrder = ['ST', 'CP', 'CR', 'CV', 'RP', 'CS', 'AE', 'WP', 'MI', 'Other'];
    const sortedEntriesByCategory = {};

    // 按照预定义顺序填充分类
    categoryOrder.forEach(category => {
      if (entriesByCategory[category] && entriesByCategory[category].length > 0) {
        sortedEntriesByCategory[category] = entriesByCategory[category];
      }
    });

    // 添加任何其他未在预定义顺序中的分类
    Object.keys(entriesByCategory).forEach(category => {
      if (!sortedEntriesByCategory[category]) {
        sortedEntriesByCategory[category] = entriesByCategory[category];
      }
    });

    // 获取保存的预设
    const savedPresets = getSavedPresets();

    // 创建弹窗
    createStatusBarManagerPopup(sortedEntriesByCategory, savedPresets);
  } catch (error) {
    console.error('[状态栏] 显示状态栏管理器失败:', error);
  }
}

