// 初始化弹窗事件处理
function initStatusBarManagerEvents() {
  const parentDoc = window.parent.document;

  // 标签切换事件
  $(parentDoc).on('click', '.tab-button', function () {
    const tab = $(this).data('tab');
    $('.tab-button', parentDoc).removeClass('active');
    $(this).addClass('active');
    $('.tab-content', parentDoc).removeClass('active');
    $(`#${tab}-tab`, parentDoc).addClass('active');

    // 如果切换到角色数据选项卡，加载角色数据
    if (tab === 'character-data') {
      loadCharacterDataList();
    }
  });

  // 全选/取消全选按钮事件
  $(parentDoc).on('click', '#toggle-all-btn', async function () {
    const $checkboxes = $('.entry-checkbox', parentDoc);
    const allChecked = $checkboxes.length === $checkboxes.filter(':checked').length;
    $checkboxes.prop('checked', !allChecked);

    // 批量更新世界书条目
    await updateAllEntriesState(!allChecked);
  });

  // 复选框变更事件 - 实时更新世界书条目
  $(parentDoc).on('change', '.entry-checkbox', async function () {
    const uid = $(this).data('uid');
    const isEnabled = $(this).prop('checked');
    const comment = $(this).data('comment');

    // 显示加载状态
    const $label = $(this).next('label');
    const originalText = $label.text();
    $label.html(`<i class="fa-solid fa-spinner fa-spin"></i> ${originalText}`);

    try {
      await updateEntryState(uid, isEnabled);
      $label.text(originalText);
    } catch (error) {
      console.error('[状态栏] 更新条目状态失败:', error);
      alert(`更新条目"${comment}"失败，请重试`);

      // 回滚UI状态但不触发change事件
      $(this).prop('checked', !isEnabled);
      $label.text(originalText);
    }
  });

  // 保存为配置按钮事件
  $(parentDoc).on('click', '#save-entries-btn', function () {
    const name = prompt('请输入配置名称:');
    if (!name) return;

    const enabledEntries = [];
    $('.entry-checkbox:checked', parentDoc).each(function () {
      enabledEntries.push(parseInt($(this).data('uid')));
    });

    if (savePreset(name, enabledEntries)) {
      alert(`配置 "${name}" 已保存。`);
      closePopup();
      showStatusBarManager(); // 刷新弹窗
    }
  });

  // 应用配置按钮事件
  $(parentDoc).on('click', '.apply-preset-btn', async function () {
    const presetName = $(this).data('name');
    console.log(`[状态栏] 用户点击应用配置按钮，配置名称: "${presetName}"`);

    const presets = getSavedPresets();
    console.log(
      '[状态栏] 当前已保存的所有配置:',
      JSON.stringify(presets.map(p => ({ name: p.name, entries: p.enabledEntries.length }))),
    );

    // 查找配置
    const preset = presets.find(p => String(p.name).trim() === String(presetName).trim());

    if (preset) {
      console.log('[状态栏] 成功找到配置:', JSON.stringify(preset));
      if (!Array.isArray(preset.enabledEntries)) {
        console.error('[状态栏] 配置格式错误，enabledEntries不是数组:', preset);
        alert('配置格式错误，无法应用');
        return;
      }

      if (confirm(`确定要应用配置 "${presetName}" 吗？这将更改所有条目的启用状态。`)) {
        // 显示加载指示
        $(this).html('<i class="fa-solid fa-spinner fa-spin"></i> 应用中...');
        $(this).prop('disabled', true);

        try {
          const success = await applyPreset(preset);
          if (success) {
            alert(`配置 "${presetName}" 已应用。`);
            closePopup();
            // 刷新状态栏显示
            await renderAllMessages();
          } else {
            $(this).html('<i class="fa-solid fa-check"></i> 应用');
            $(this).prop('disabled', false);
            alert('应用配置失败，请重试。');
          }
        } catch (error) {
          console.error('[状态栏] 应用配置时发生错误:', error);
          $(this).html('<i class="fa-solid fa-check"></i> 应用');
          $(this).prop('disabled', false);
          alert('应用配置时发生错误: ' + error.message);
        }
      }
    } else {
      console.warn(`[状态栏] 未能找到配置 "${presetName}"`);
      alert(`找不到配置 "${presetName}"，可能已被删除或名称不匹配。请检查配置列表。`);
    }
  });

  // 删除配置按钮事件
  $(parentDoc).on('click', '.delete-preset-btn', function (e) {
    // 阻止事件冒泡，防止触发其他事件
    e.preventDefault();
    e.stopPropagation();

    const presetName = $(this).data('name');
    console.log(`[状态栏] 用户点击删除配置按钮，配置名称: "${presetName}"`);

    if (confirm(`确定要删除配置 "${presetName}" 吗？此操作不可撤销。`)) {
      try {
        if (deletePreset(presetName)) {
          console.log(`[状态栏] 成功删除配置: "${presetName}"`);
          $(`.preset-item[data-name="${presetName}"]`, parentDoc).remove();

          // 如果没有配置了，显示空消息
          if ($('.preset-item', parentDoc).length === 0) {
            $('#presets-tab', parentDoc).html(
              '<p style="font-style: italic; color: #999; text-align: center; margin: 20px 0;">尚未保存任何配置</p>',
            );
          }
        } else {
          console.error(`[状态栏] 删除配置失败: "${presetName}"`);
          alert('删除配置失败，请重试。');
        }
      } catch (error) {
        console.error(`[状态栏] 删除配置时发生错误:`, error);
        alert(`删除配置时发生错误: ${error.message}`);
      }
    }
  });

  // 修复展开/折叠详情按钮事件 - 防止冒泡并修正详情加载逻辑
  $(parentDoc).on('click', '.toggle-preset-details-btn', function (e) {
    // 阻止事件冒泡，防止触发父元素的点击事件
    e.preventDefault();
    e.stopPropagation();

    const $btn = $(this);
    const $icon = $btn.find('i');
    const $presetItem = $btn.closest('.preset-item');
    const $detailsSection = $presetItem.find('.preset-details');
    const presetName = $presetItem.data('name');

    console.log(
      `[状态栏] 预设详情切换: "${presetName}", 当前状态: ${$detailsSection.is(':visible') ? '展开' : '折叠'}`,
    );

    // 切换展开/折叠状态 - 不使用动画，直接显示/隐藏以避免冲突
    if ($detailsSection.is(':visible')) {
      $detailsSection.hide();
      $icon.removeClass('fa-chevron-up').addClass('fa-chevron-down');
      console.log(`[状态栏] 预设详情已折叠: "${presetName}"`);
    } else {
      // 先直接显示，再加载内容
      $detailsSection.show();
      $icon.removeClass('fa-chevron-down').addClass('fa-chevron-up');
      console.log(`[状态栏] 预设详情已展开: "${presetName}"`);

      // 显示加载状态
      $detailsSection.html(
        '<p style="text-align: center; margin: 15px 0;"><i class="fa-solid fa-spinner fa-spin"></i> 加载条目中...</p>',
      );

      // 异步加载详情数据
      loadPresetDetails(presetName, $detailsSection);

      // 添加一个标记，表示此项已展开
      $presetItem.attr('data-expanded', 'true');
    }
  });

  // 配置条目复选框变更事件
  $(parentDoc).on('change', '.preset-entry-checkbox', function () {
    const uid = parseInt($(this).data('uid'));
    const presetName = $(this).data('preset');
    const isEnabled = $(this).prop('checked');

    // 获取配置
    const presets = getSavedPresets();
    const presetIndex = presets.findIndex(p => String(p.name).trim() === String(presetName).trim());

    if (presetIndex === -1) {
      alert(`无法更新配置: 找不到配置 "${presetName}"`);
      return;
    }

    // 克隆配置对象
    const updatedPreset = { ...presets[presetIndex] };

    // 确保enabledEntries是数组
    if (!Array.isArray(updatedPreset.enabledEntries)) {
      updatedPreset.enabledEntries = [];
    }

    // 根据复选框状态更新enabledEntries
    if (isEnabled) {
      // 添加条目UID（如果不存在）
      if (!updatedPreset.enabledEntries.includes(uid)) {
        updatedPreset.enabledEntries.push(uid);
      }
    } else {
      // 移除条目UID
      updatedPreset.enabledEntries = updatedPreset.enabledEntries.filter(id => id !== uid);
    }

    // 更新时间戳
    updatedPreset.timestamp = Date.now();

    // 更新配置列表
    presets[presetIndex] = updatedPreset;

    // 保存到本地存储
    try {
      localStorage.setItem(PRESET_STORAGE_KEY, JSON.stringify(presets));

      // 更新配置项信息
      const $presetItem = $(this).closest('.preset-item');
      $presetItem
        .find('.preset-info div:nth-child(2)')
        .text(
          `启用条目: ${updatedPreset.enabledEntries.length} 个，创建: ${new Date(
            updatedPreset.timestamp,
          ).toLocaleString()}`,
        );

      console.log(`[状态栏] 配置 "${presetName}" 已更新，条目 ${uid} ${isEnabled ? '添加' : '移除'}`);
    } catch (error) {
      console.error('[状态栏] 更新配置失败:', error);
      alert(`更新配置失败: ${error.message}`);
      // 回滚UI状态
      $(this).prop('checked', !isEnabled);
    }
  });

  // 角色数据相关事件处理
  initCharacterDataEvents();
}

// 新增异步加载预设详情函数
async function loadPresetDetails(presetName, $detailsSection) {
  try {
    console.log(`[状态栏] 开始加载预设详情: "${presetName}"`);

    // 获取配置
    const presets = getSavedPresets();
    const preset = presets.find(p => String(p.name).trim() === String(presetName).trim());

    if (!preset) {
      $detailsSection.html('<p style="color: #f55; margin: 10px 0;">无法加载配置详情: 配置不存在</p>');
      return;
    }

    // 确保enabledEntries是数组
    if (!Array.isArray(preset.enabledEntries)) {
      preset.enabledEntries = [];
      console.warn(`[状态栏] 预设 "${presetName}" 的enabledEntries不是数组，已重置为空数组`);
    }

    // 获取所有条目
    const entries = await getLorebookEntries(lorebook_name);
    console.log(`[状态栏] 获取Lorebook条目成功，总数: ${entries.length}`);

    // 先对条目进行排序 - 优先使用display_index，其次使用comment
    entries.sort((a, b) => {
      // 首先检查是否设置条目，如果是，放到最后
      if (a.comment.startsWith('设置-') && !b.comment.startsWith('设置-')) return 1;
      if (!a.comment.startsWith('设置-') && b.comment.startsWith('设置-')) return -1;

      // 尝试使用display_index排序
      if (a.display_index !== undefined && b.display_index !== undefined) {
        return a.display_index - b.display_index;
      }

      // 如果没有display_index，则按注释文本排序
      return a.comment.localeCompare(b.comment, 'zh-CN');
    });

    // 过滤并按类别分组条目
    const entriesByCategory = {};
    entries.forEach(entry => {
      if (entry.comment.startsWith('设置-')) return; // 忽略设置条目

      // 尝试从注释中提取类别，格式如: [ST|时间] 的 ST 部分
      let category = 'Other';
      const categoryMatch = entry.comment.match(/\[(\w+)\|/);
      if (categoryMatch) {
        category = categoryMatch[1];
      }

      if (!entriesByCategory[category]) {
        entriesByCategory[category] = [];
      }

      // 检查条目是否在配置中启用
      const isEnabled = preset.enabledEntries.includes(parseInt(entry.uid));
      entriesByCategory[category].push({
        ...entry,
        isInPreset: isEnabled,
      });
    });

    // 按类别优先级排序
    const categoryOrder = ['ST', 'CP', 'CR', 'CV', 'RP', 'CS', 'AE', 'WP', 'MI', 'Other'];
    const sortedCategories = Object.keys(entriesByCategory).sort((a, b) => {
      const indexA = categoryOrder.indexOf(a);
      const indexB = categoryOrder.indexOf(b);
      return (indexA === -1 ? 999 : indexA) - (indexB === -1 ? 999 : indexB);
    });

    // 构建条目列表HTML
    let detailsHtml = '';
    sortedCategories.forEach(category => {
      // 获取类别友好名称
      const categoryInfo = CATEGORY_MAPPING[category] || { name: category, icon: 'fa-circle-info' };
      const categoryEntries = entriesByCategory[category];

      detailsHtml += `
        <div class="preset-entry-category">
          <i class="fa-solid ${categoryInfo.icon}" style="margin-right: 5px; opacity: 0.7;"></i>
          ${categoryInfo.name || category} (${categoryEntries.length})
        </div>
      `;

      // 条目列表
      categoryEntries.forEach(entry => {
        detailsHtml += `
          <div class="preset-entry-item">
            <input type="checkbox" class="preset-entry-checkbox" id="preset-entry-${presetName}-${entry.uid}" 
              data-uid="${entry.uid}" data-preset="${presetName}" ${entry.isInPreset ? 'checked' : ''}>
            <label for="preset-entry-${presetName}-${entry.uid}" style="font-size: 13px;">${entry.comment}</label>
          </div>
        `;
      });
    });

    // 更新详情区域
    if (detailsHtml) {
      $detailsSection.html(`
        <div style="margin-bottom: 10px;">
          <div class="preset-summary-info">
            <div style="font-size: 13px; margin-bottom: 8px; display: flex; justify-content: space-between; align-items: center;">
              <span>点击复选框可以调整该配置中的条目:</span>
              <span class="preset-count-info">${preset.enabledEntries.length}个启用 / ${
        entries.length - getLorebookSettingsEntriesCount(entries)
      }个条目</span>
            </div>
          </div>
          <div class="preset-entries-list">${detailsHtml}</div>
        </div>
      `);
    } else {
      $detailsSection.html('<p style="color: #888; margin: 10px 0; font-style: italic;">此配置不包含任何条目</p>');
    }

    console.log(`[状态栏] 预设详情加载完成: "${presetName}"`);
  } catch (error) {
    console.error('[状态栏] 加载配置详情失败:', error);
    $detailsSection.html(`<p style="color: #f55; margin: 10px 0;">加载失败: ${error.message}</p>`);
  }
}

// 获取设置条目数量的辅助函数
function getLorebookSettingsEntriesCount(entries) {
  return entries.filter(entry => entry.comment.startsWith('设置-')).length;
}

// 创建弹窗函数
function createPopup(content) {
  const parentDoc = window.parent.document;

  // 创建遮罩层
  const $overlay = $('<div></div>')
    .attr('id', `${POPUP_ID}-overlay`)
    .css({
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      zIndex: 1000,
      backdropFilter: 'blur(2px)',
    })
    .on('click', function (e) {
      if (e.target === this) {
        closePopup();
      }
    });

  // 创建弹窗
  const $popup = $('<div></div>')
    .attr('id', POPUP_ID)
    .css({
      position: 'fixed',
      top: '70px',
      left: '50%',
      transform: 'translateX(-50%)',
      width: '700px',
      maxWidth: '90%',
      maxHeight: '85vh', // 增加最大高度
      background: '#262626',
      color: '#e0e0e0',
      border: 'none',
      borderRadius: '8px',
      boxShadow: '0 15px 30px rgba(0, 0, 0, 0.6)',
      padding: '20px',
      zIndex: 1001,
      boxSizing: 'border-box',
      overflow: 'hidden',
      display: 'flex',
      flexDirection: 'column', // 使用弹性布局
      fontFamily: 'system-ui, -apple-system, sans-serif',
      animation: 'fadeIn 0.2s ease-out',
    })
    .html(content);

  // 动画样式
  const styleElement = document.createElement('style');
  styleElement.id = `${POPUP_ID}-animation-style`;
  styleElement.textContent = `
    @keyframes fadeIn {
      from { opacity: 0; transform: translateX(-50%) translateY(-10px) scale(0.98); }
      to { opacity: 1; transform: translateX(-50%) translateY(0) scale(1); }
    }
  `;

  // 添加到DOM
  parentDoc.head.appendChild(styleElement);
  $('body', parentDoc).append($overlay).append($popup);

  // 绑定关闭按钮事件
  $(`#close-${POPUP_ID}, #cancel-${POPUP_ID}`, parentDoc).on('click', closePopup);
}

// 关闭弹窗函数
function closePopup() {
  const parentDoc = window.parent.document;

  // 移除事件监听
  $(parentDoc).off('click', '.tab-button');
  $(parentDoc).off('click', '#toggle-all-btn');
  $(parentDoc).off('click', '#save-entries-btn');
  $(parentDoc).off('click', '.apply-preset-btn');
  $(parentDoc).off('click', '.delete-preset-btn');

  // 移除DOM元素
  $(`#${POPUP_ID}`, parentDoc).remove();
  $(`#${POPUP_ID}-overlay`, parentDoc).remove();
  $(`#${POPUP_ID}-animation-style`, parentDoc.head).remove();
}

// 初始化
$(async () => {
  try {
    await errorCatched(option.update)();
    await errorCatched(render.update)();

    // 使用设置的初始化延迟
    const initDelay = option.option?.init_delay || 0;
    if (initDelay > 0) {
      console.log(`[状态栏] 将在${initDelay}毫秒后初始化渲染`);
      setTimeout(() => {
        errorCatched(renderAllMessages)();
      }, initDelay);
    } else {
      await errorCatched(renderAllMessages)();
    }

    // 注册事件监听
    eventOn(tavern_events.CHARACTER_MESSAGE_RENDERED, errorCatched(renderOneMessage));
    eventOn(tavern_events.MESSAGE_UPDATED, errorCatched(renderOneMessage));
    eventOn(tavern_events.MESSAGE_SWIPED, errorCatched(renderOneMessage));
    eventOn(tavern_events.MESSAGE_DELETED, errorCatched(renderAllMessages));

    // 监听APP_READY事件，用于创建菜单按钮
    eventOn(tavern_events.APP_READY, errorCatched(createMenuButton));

    // 监听世界书更新事件
    eventOn(
      tavern_events.WORLDINFO_UPDATED,
      errorCatched(async lorebook => {
        if (lorebook !== lorebook_name) {
          return;
        }

        const optionChanged = await option.update();
        const styleChanged = await render.update();

        if (optionChanged || styleChanged) {
          // 应用暗色模式
          if (option.option?.dark_mode) {
            $('body', window.parent.document).addClass('dark-mode');
          } else {
            $('body', window.parent.document).removeClass('dark-mode');
          }

          await renderAllMessages();
        }
      }),
    );

    // 应用暗色模式
    if (option.option?.dark_mode) {
      $('body', window.parent.document).addClass('dark-mode');
    }

    // 创建菜单按钮
    createMenuButton();

    console.log('[状态栏] 已加载');
  } catch (error) {
    console.error('[状态栏] 加载失败:', error);
  }
});

