// 添加数据持久化功能
// 保存角色关系参数和基本信息到聊天变量中
async function saveCharacterDataToVariables(parsedData) {
  try {
    // 首先获取角色名称，这是必需的
    let characterName = '';
    if (parsedData.categories['CP']) {
      const nameItem = parsedData.categories['CP'].find(item => item.key === '名字');
      if (nameItem && nameItem.values && nameItem.values.length > 0) {
        characterName = nameItem.values[0];

        // 如果名字是数组（含@分隔），取第一个元素
        if (Array.isArray(characterName)) {
          characterName = characterName[0];
        }
      }
    }

    // 如果没有找到角色名，不进行保存
    if (!characterName) {
      console.warn('[状态栏] 未找到角色名，无法保存角色数据');
      return;
    }

    // 构建要保存的角色数据对象
    let characterData = {};

    // 1. 保存基本信息 (年龄、身高、特征、永久性状态)
    if (parsedData.categories['CP']) {
      const basicInfo = {};

      // 保存年龄
      const ageItem = parsedData.categories['CP'].find(item => item.key === '年龄');
      if (ageItem && ageItem.values && ageItem.values.length > 0) {
        basicInfo.age = ageItem.values[0];
      }

      // 保存身高
      const heightItem = parsedData.categories['CP'].find(item => item.key === '身高');
      if (heightItem && heightItem.values && heightItem.values.length > 0) {
        basicInfo.height = heightItem.values[0];
      }

      // 保存特征
      const traitsItem = parsedData.categories['CP'].find(item => item.key === '特征');
      if (traitsItem && traitsItem.values && traitsItem.values.length > 0) {
        basicInfo.traits = traitsItem.values[0];
      }

      // 保存永久性状态
      const permanentItem = parsedData.categories['CP'].find(item => item.key === '永久性状态');
      if (permanentItem && permanentItem.values && permanentItem.values.length > 0) {
        basicInfo.permanent = permanentItem.values[0];
      }

      characterData.basicInfo = basicInfo;
    }

    // 2. 保存角色关系参数 (RP类别)
    if (parsedData.categories['RP']) {
      const relationshipParameters = {};

      // 遍历所有关系参数
      parsedData.categories['RP'].forEach(item => {
        // 只存储键、当前值、最大值和阶段
        const paramData = {
          key: item.key,
          currentValue: 0,
          maxValue: 100,
          stage: '',
        };

        // 解析当前值和最大值
        if (item.values[0]) {
          if (Array.isArray(item.values[0])) {
            // 如果是数组（含@分隔）
            const [current, max] = item.values[0];
            paramData.currentValue = parseInt(current) || 0;
            paramData.maxValue = parseInt(max) || 100;
          } else {
            // 如果是单个值
            paramData.currentValue = parseInt(item.values[0]) || 0;
          }
        }

        // 保存阶段 (values[3])
        if (item.values.length > 3) {
          paramData.stage = item.values[3];
        }

        // 存储参数
        relationshipParameters[item.key] = paramData;
      });

      characterData.relationshipParameters = relationshipParameters;
    }

    // 3. 使用聊天变量API保存数据
    const chatVariables = getVariables({ type: 'chat' });

    // 创建或更新角色数据存储
    if (!chatVariables.characterData) {
      chatVariables.characterData = {};
    }

    // 保存/更新当前角色的数据
    chatVariables.characterData[characterName] = characterData;

    // 保存回聊天变量
    await replaceVariables(chatVariables, { type: 'chat' });

    console.log(`[状态栏] 成功保存角色 "${characterName}" 的数据到聊天变量`);
  } catch (error) {
    console.error('[状态栏] 保存角色数据到聊天变量时出错:', error);
  }
}

// 初始化角色数据相关事件处理
function initCharacterDataEvents() {
  const parentDoc = window.parent.document;

  // 刷新按钮事件
  $(parentDoc).on('click', '#refresh-character-data-btn', function () {
    loadCharacterDataList();
  });

  // 角色列表项点击事件
  $(parentDoc).on('click', '.character-list-item', function () {
    const characterName = $(this).data('name');
    $('.character-list-item', parentDoc).removeClass('active');
    $(this).addClass('active');
    loadCharacterDetail(characterName);
  });

  // 添加参数按钮事件
  $(parentDoc).on('click', '#add-param-btn', function () {
    addEmptyRelationshipParam();
  });

  // 删除参数按钮事件
  $(parentDoc).on('click', '.param-remove', function () {
    $(this).closest('.relationship-param').remove();
  });

  // 保存角色数据按钮事件
  $(parentDoc).on('click', '#save-character-data-btn', function () {
    saveCharacterDetailChanges();
  });

  // 删除角色数据按钮事件
  $(parentDoc).on('click', '#delete-character-data-btn', function () {
    const characterName = $('#character-name', parentDoc).text();
    if (confirm(`确定要删除角色 "${characterName}" 的数据吗？此操作不可撤销。`)) {
      deleteCharacterData(characterName);
    }
  });
}

// 加载角色数据列表
async function loadCharacterDataList() {
  try {
    const parentDoc = window.parent.document;
    const $characterList = $('#character-data-list', parentDoc);

    // 显示加载中
    $characterList.html('<p class="loading-text">加载中...</p>');

    // 从聊天变量中获取角色数据
    const chatVariables = getVariables({ type: 'chat' });
    const characterData = chatVariables.characterData || {};

    // 更新列表
    if (Object.keys(characterData).length === 0) {
      $characterList.html('<p class="character-no-data">暂无保存的角色数据</p>');
      $('#character-data-empty-state', parentDoc).show();
      $('#character-data-detail', parentDoc).hide();
      return;
    }

    // 生成角色列表HTML
    let listHtml = '';
    Object.keys(characterData).forEach(name => {
      listHtml += `<div class="character-list-item" data-name="${name}">${name}</div>`;
    });

    $characterList.html(listHtml);
  } catch (error) {
    console.error('[状态栏] 加载角色数据列表失败:', error);
    const parentDoc = window.parent.document;
    $('#character-data-list', parentDoc).html('<p class="character-no-data">加载失败，请重试</p>');
  }
}

// 加载角色详情
async function loadCharacterDetail(characterName) {
  try {
    const parentDoc = window.parent.document;

    // 从聊天变量中获取角色数据
    const chatVariables = getVariables({ type: 'chat' });
    const characterData = chatVariables.characterData || {};
    const charData = characterData[characterName];

    if (!charData) {
      console.error(`[状态栏] 未找到角色 "${characterName}" 的数据`);
      return;
    }

    // 显示详情面板，隐藏空状态
    $('#character-data-empty-state', parentDoc).hide();
    $('#character-data-detail', parentDoc).show();

    // 设置角色名
    $('#character-name', parentDoc).text(characterName);

    // 填充基本信息
    $('#character-age', parentDoc).val(charData.basicInfo?.age || '');
    $('#character-height', parentDoc).val(charData.basicInfo?.height || '');

    // 处理特征和永久性状态，可能是字符串或数组
    let traits = charData.basicInfo?.traits || '';
    if (Array.isArray(traits)) {
      traits = traits.join('@');
    }

    let permanent = charData.basicInfo?.permanent || '';
    if (Array.isArray(permanent)) {
      permanent = permanent.join('@');
    }

    $('#character-traits', parentDoc).val(traits);
    $('#character-permanent', parentDoc).val(permanent);

    // 渲染关系参数
    renderRelationshipParams(charData.relationshipParameters);
  } catch (error) {
    console.error('[状态栏] 加载角色详情失败:', error);
  }
}

// 渲染关系参数
function renderRelationshipParams(params) {
  const parentDoc = window.parent.document;
  const $container = $('#relationship-params-container', parentDoc);

  // 清空容器
  $container.empty();

  if (!params || Object.keys(params).length === 0) {
    $container.html('<p class="character-no-data">暂无关系参数</p>');
    return;
  }

  // 遍历所有参数并渲染
  Object.values(params).forEach(param => {
    $container.append(createRelationshipParamHTML(param.key, param.currentValue, param.maxValue, param.stage));
  });
}

// 创建关系参数HTML
function createRelationshipParamHTML(key, currentValue, maxValue, stage) {
  return `
    <div class="relationship-param" data-key="${key}">
      <div class="param-header">
        <div class="param-name">${key}</div>
        <button class="param-remove"><i class="fa-solid fa-times"></i></button>
      </div>
      <div class="param-fields">
        <div class="character-field">
          <label>参数名称</label>
          <input type="text" class="character-input param-key" value="${key || ''}">
        </div>
        <div class="character-field">
          <label>阶段描述</label>
          <input type="text" class="character-input param-stage" value="${stage || ''}">
        </div>
        <div class="character-field">
          <label>当前值</label>
          <input type="number" class="character-input param-current" min="0" max="100" value="${currentValue || 0}">
        </div>
        <div class="character-field">
          <label>最大值</label>
          <input type="number" class="character-input param-max" min="1" max="100" value="${maxValue || 100}">
        </div>
      </div>
    </div>
  `;
}

// 添加空的关系参数
function addEmptyRelationshipParam() {
  const parentDoc = window.parent.document;
  const $container = $('#relationship-params-container', parentDoc);

  // 如果容器中有"暂无关系参数"的提示，则移除
  const $noData = $container.find('.character-no-data');
  if ($noData.length > 0) {
    $noData.remove();
  }

  // 添加一个空的参数表单
  $container.append(createRelationshipParamHTML('新参数', 0, 100, ''));
}

// 保存角色详情更改
async function saveCharacterDetailChanges() {
  try {
    const parentDoc = window.parent.document;
    const characterName = $('#character-name', parentDoc).text();

    // 从聊天变量中获取现有角色数据
    const chatVariables = getVariables({ type: 'chat' });
    if (!chatVariables.characterData) {
      chatVariables.characterData = {};
    }

    // 准备新的角色数据对象
    const characterData = {
      basicInfo: {
        age: $('#character-age', parentDoc).val(),
        height: $('#character-height', parentDoc).val(),
        traits: $('#character-traits', parentDoc).val(),
        permanent: $('#character-permanent', parentDoc).val(),
      },
      relationshipParameters: {},
    };

    // 收集所有关系参数
    $('.relationship-param', parentDoc).each(function () {
      const $param = $(this);
      const key = $param.find('.param-key').val();

      // 如果参数名为空，跳过
      if (!key || key.trim() === '') {
        return;
      }

      const currentValue = parseInt($param.find('.param-current').val()) || 0;
      const maxValue = parseInt($param.find('.param-max').val()) || 100;
      const stage = $param.find('.param-stage').val() || '';

      characterData.relationshipParameters[key] = {
        key,
        currentValue,
        maxValue,
        stage,
      };
    });

    // 更新聊天变量中的角色数据
    chatVariables.characterData[characterName] = characterData;
    await replaceVariables(chatVariables, { type: 'chat' });

    // 显示保存成功提示
    const $saveBtn = $('#save-character-data-btn', parentDoc);
    const originalText = $saveBtn.html();
    $saveBtn.html('<i class="fa-solid fa-check"></i> 已保存');
    setTimeout(() => {
      $saveBtn.html(originalText);
    }, 2000);

    console.log(`[状态栏] 成功更新角色 "${characterName}" 的数据`);
  } catch (error) {
    console.error('[状态栏] 保存角色详情失败:', error);
    alert(`保存失败: ${error.message}`);
  }
}

// 删除角色数据
async function deleteCharacterData(characterName) {
  try {
    const parentDoc = window.parent.document;

    // 从聊天变量中获取角色数据
    const chatVariables = getVariables({ type: 'chat' });

    if (!chatVariables.characterData || !chatVariables.characterData[characterName]) {
      console.warn(`[状态栏] 未找到角色 "${characterName}" 的数据，无需删除`);
      return;
    }

    // 删除指定角色的数据
    delete chatVariables.characterData[characterName];
    await replaceVariables(chatVariables, { type: 'chat' });

    // 刷新角色列表并重置详情视图
    loadCharacterDataList();
    $('#character-data-empty-state', parentDoc).show();
    $('#character-data-detail', parentDoc).hide();

    console.log(`[状态栏] 成功删除角色 "${characterName}" 的数据`);
  } catch (error) {
    console.error('[状态栏] 删除角色数据失败:', error);
    alert(`删除失败: ${error.message}`);
  }
}

