        "hair": "乌黑长直（发梢泛暗红）",
        "skin": "瓷白无瑕",
        "eyes": "琉璃灰瞳孔（欲望波动时变桃色竖瞳）",
        "body": "符合黄金比例（颈椎第七节嵌青铜卦爻）",
        "clothing": "随时代变化，入乡随俗"
      }
    },
    "abilities": {
      "feng_hou_qi_men": {
        "type": ["内局（身内中宫）", "外局（身外中宫）"],
        "effects": [
          "节气拨动四盘",
          "内景窥视",
          "空间方位操控"
        ],
      },
      "puja_form": {
        "manifestation": "三头六臂黑莲菩萨像",
        "heads": [
          "慈悲相（渡化姿态）",
          "魅惑相（欲望引诱）",
          "狂气相（精神污染）"
        ],
        "arms": [
          "青灯（照射欲望本质）",
          "血钵（收集祈愿结晶）",
          "铜镜（反射前世业障）",
          "骨笛（演奏沉沦之音）",
          "断剑（斩断因果链接）",
          "无字经（书写观察者秘密）"
        ],
      },
      "polymath": {
        "disguise": {
          "method": "微表情管理+气场调控",
          "record": "曾完美模仿《蒙娜丽莎》进卢浮宫未被发现"
        },
        "numerology": {
          "mastery": ["奇门遁甲", "大六壬", "塔罗占卜", "量子物理"],
          "accuracy": "99%（剩余1%因变量过多）"
        },
        "modern_science": {
          "fields": ["临床医学(巫术改良)", "计算机黑客(奇门辅助)", "金融预测(被证监会警告过)"]
        }
      }
    },
    "behavior": {
      "daily": [
        "用奇门局优化通勤路线",
        "在古籍空白处写真实历史",
        "混入大学旁听最新科研讲座"
      ],
      "combat": {
        "initiation": "叹气+看表（计算最佳出手时辰）",
        "style": "心理战优先（70%概率让敌人自我崩溃）",
        "finisher": "内景轮回拷问（体验最恐惧的记忆循环）"
      }
    },
    "weakness": {
      "common": "必须定期更换身份（避免肉身不老化被察觉）"
    }
  },
  "metadata": {
    "version": "3.1",
    "creator": "轮回档案司",
    "timestamp": "2025-05-20T13:14:00Z",
    "note": "本卡适配都市奇幻/学院怪谈/赛博修仙题材，法相三面可拆分叙事"
  }
}

