.chat-message, .mes {
    position: relative;
    overflow: visible !important;
}

@keyframes sway {
    0% { transform: rotate(0deg); }
    25% { transform: rotate(-8deg); }
    50% { transform: rotate(8deg); }
    75% { transform: rotate(-8deg); }
    100% { transform: rotate(0deg); }
}

.chat-message.user::after, 
.mes[is_user="true"]::after {
    content: "";
    display: block;
    width: 45px;
    height: 45px;
    background: url('https://files.catbox.moe/c4pfxf.png') no-repeat center;
    background-size: contain;
    position: absolute;
    bottom: 5px;
    right: 5px;
    opacity: 0.9;
    pointer-events: none;
    animation: sway 3s infinite ease-in-out;
}

.chat-message.bot::after, 
.mes:not([is_user="true"])::after {
    content: "";
    display: block;
    width: 45px;
    height: 45px;
    background: url('https://files.catbox.moe/3aoq9m.png') no-repeat center;
    background-size: contain;
    position: absolute;
    bottom: 5px;
    left: 5px;
    opacity: 0.9;
    pointer-events: none;
    animation: sway 3s infinite ease-in-out;
}
/* 确保导航栏图标对齐 */
#leftNavDrawerIcon, 
#sys-settings-button .drawer-icon, 
#advanced-formatting-button .drawer-icon, 
#WI-SP-button .drawer-icon, 
#user-settings-button .drawer-icon, 
#logo_block .drawer-icon, 
#extensions-settings-button .drawer-icon, 
#persona-management-button .drawer-icon, 
#rightNavDrawerIcon {
    position: relative; 
}

/* 统一调整所有图标的位置和透明度 */
#leftNavDrawerIcon::before,
#sys-settings-button .drawer-icon::before,
#advanced-formatting-button .drawer-icon::before,
#WI-SP-button .drawer-icon::before,
#user-settings-button .drawer-icon::before,
#logo_block .drawer-icon::before,
#extensions-settings-button .drawer-icon::before,
#persona-management-button .drawer-icon::before,
#rightNavDrawerIcon::before {
    content: "";
    position: absolute;
    left: 5px; /* 向左移动 */
    top: 50%;
    transform: translateY(-50%);
    width: 50px;
    height: 30px;
    background-size: contain;
    background-repeat: no-repeat;
    opacity: 1; /* 取消透明感 */
    filter: saturate(1.4); /* 增加饱和度 */
}

/* 单独设置每个图标的背景 */
