custom_css:
@import url('https://static.zeoseven.com/zsft/7/main/result.css');

body {
   font-family: "Zhuque Fangsong (technical preview)";
   font-weight: normal;
}
/* 中文字体应用 */
body:lang(zh) {
   font-family: "Zhuque Fangsong (technical preview)";
}

-------------------------------------
/* Moaisha❥ */

/* 基础气泡样式 */
body.bubblechat .mes_block {
    position: relative;
    border-radius: 5px; 
    max-width: 100%; 
    width: fit-content !important; 
}
/* 非用户消息气泡样式 */
body.bubblechat .mes:not([is_user="true"]) {
background: linear-gradient(140deg,
    rgba(181,201,158, 0.5)86%,
    rgba(255, 255, 255, 0.5) 14%);
    backdrop-filter: blur(8px);
    transition: all 0.3s ease;  
}
/* 非用户引用文本 */
body.bubblechat .mes:not([is_user="true"]) q {
font-family: "Zhuque Fangsong (technical preview)";
    font-size: 16px;
    font-style: italic;  
    color: #474F1E; 
}

/* 用户引用文本 */
body.bubblechat .mes[is_user="true"] q {
font-family: "Zhuque Fangsong (technical preview)";
    font-size: 16px;
    font-style: italic;  
    color: #474F1E; 
}
/* 代码块文本背景颜色（`包裹的文本） */
body.bubblechat .mes code {
    border-radius: 15px;
    padding: 2px 4px; 
    background-color: rgba(171, 192, 147, 1);  
    color: #FFFFFF;  
    border-color: rgba(171, 192, 147, 1);
}
/* 设置文本框的背景和文字颜色 */
 .text_pole, textarea {
    background-color:rgba(254, 243, 201, 0.75); 
    color: #000000; 
}
/*删除按钮 */
#delete_button, .redWarningBG, .menu_button.popup-button-ok {
    background: linear-gradient(to bottom, 
                rgba(71,79,30, 0.8), 
                rgba(101,107,83, 0.8)) !important; 
}
/* 移动端滚动条隐藏 */
@media screen and (max-width: 1000px) {
    *::-webkit-scrollbar {
        display: none;
    }

    * {
        scrollbar-width: none;
        -ms-overflow-style: none;
    }
}
/* 设置标题底色去除 */
.title_restorable {
  background-image: none !important; 
}

pre {
    font-family: monospace;
    padding: 10px;
    border-radius: 5px;
    background-clip: padding-box;
    position: relative;
}
/* 设置标题字体修改 */
#extensions_settings .inline-drawer-toggle.inline-drawer-header, 
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header, 
#user-settings-block h4, 
.standoutHeader {
font-family: "Zhuque Fangsong (technical preview)";
  font-weight: normal;
  background-image: none !important; 
  background-color: rgba(255, 250, 205, 0.5); 
  text-shadow: 1px 1px 2px rgba(255, 255, 255, 0.5);
}
/* 编辑框黑色去除 */
.edit_textarea {
    background-color: transparent !important; 
}

body.bubblechat .mes_block {
  padding-bottom: unset;
}

/* 隐藏代理警告 */
#ReverseProxyWarningMessage { display: none !important; }

/* 标注区域 */
.mes_text blockquote {
    background-color: rgba(255, 250, 205, 0.3);
    border-left: 5px solid #e0f2f7; 
    color: #388e3c; 
}
