@import url('https://static.zeoseven.com/zsft/7/main/result.css');

body {
   font-family: "Zhuque Fangsong (technical preview)";
   font-weight: normal;
}
/* 中文字体应用 */
body:lang(zh) {
   font-family: "Zhuque Fangsong (technical preview)";
}


:root {
    --send-but-bg-color: #8b735b;
    --send-but-box-shadow-color: #4d3e2e;
    --send-but-inset-shadow-color: rgba(255,255,255,0.2);
    --delete-button-bg-gradient-start: #8b735b;
    --delete-button-bg-gradient-end: #8b735b;
    --text-pole-bg-color: #f5ebdc;
    --text-pole-bg-gradient-start: rgba(245,235,220,0.9);
    --text-pole-bg-gradient-end: rgba(230,215,195,0.9);
    --text-pole-font-color: #4d3e2e;
    --prompt-disabled-font-color: #766230;
    --mes-time-font-color: #766230;
    --mes-time-text-shadow-color: rgba(118,98,72,0.15);
    --title-restorable-bg-color: rgba(230,215,195,0.9);
    --settings-title-bg-color: rgba(245,235,220,0.95);
    --settings-title-font-color: #4d3e2e;
    --settings-title-text-shadow-color: rgba(118,98,72,0.2);
    --edit-textarea-bg-color: #f5ebdc;
    --blockquote-bg-color: rgba(245,235,220,0.7);
    --blockquote-border-color: #8b735b;
    --blockquote-font-color: #4d3e2e;
    --waifu-chat-and-scrollbar: 2px solid #8b735b;
    --ink-spot-color: rgba(118,98,72,0.1);
    --avatar-border-color: #8b735b;
    --avatar-size: 50px;
    --mes-padding-top: 50px;
    --mes-margin-bottom: 25px;
    --mes-text-margin: 25px 15px 15px;
    --mes-text-padding: 20px 25px;
    --mes-text-border-width: 2px;
    --mes-text-border-color: #8b735b;
    --mes-text-border-radius: 8px 0 8px 0;
    --mes-text-box-shadow-1: 3px 3px 8px rgba(118,98,72,0.1);
    --mes-text-box-shadow-2: inset 1px 1px 3px rgba(255,255,255,0.2);
    --mes-text-border-outside-width: 1px;
    --mes-text-border-outside-color: rgba(139,115,91,0.3);
    --mes-text-border-outside-radius: 12px 2px 12px 2px;
    --send-but-padding: 8px 16px;
    --send-but-border-radius: 8px;
    --text-pole-border-radius: 15px;
    --text-pole-border-width: 2px;
    --text-pole-border-color: #766230;
    --text-pole-box-shadow-1: inset 2px 2px 5px rgba(118,98,72,0.1);
    --text-pole-box-shadow-2: 2px 2px 5px rgba(118,98,72,0.2);
    --delete-button-border-width: 2px;
    --delete-button-border-radius: 0 8px 0 8px;
    --delete-button-box-shadow-1: 3px 3px 5px rgba(0,0,0,0.3);
    --delete-button-box-shadow-2: inset 0 0 5px rgba(255,215,0,0.2);
    --title-restorable-box-shadow: 0 2px 4px rgba(118,98,72,0.1);
    --settings-title-border-left-width: 5px;
    --settings-title-padding: 10px 20px;
    --pre-bg-gradient-start: #8b735b;
    --pre-bg-gradient-end: #5C442E;
    --pre-border-width: 3px;
    --pre-border-color: #785447;
    --pre-border-radius: 8px 0 8px 0;
    --pre-box-shadow-1: 5px 5px 15px rgba(0,0,0,0.3);
    --pre-box-shadow-2: inset 0 0 10px rgba(255,255,255,0.1);
    --pre-border-outside-width: 2px;
    --pre-border-outside-color: #8b735b;
    --pre-border-outside-radius: 10px 0 10px 0;
    --top-settings-holder-bg-gradient-start: rgba(245,235,220,0.95);
    --top-settings-holder-bg-gradient-end: rgba(230,215,195,0.93);
    --top-settings-holder-box-shadow: 0 2px 5px rgba(118,98,72,0.15);
    --drawer-icon-color: #4d3e2e;
    --drawer-icon-font-size: 1.5em;
    --mes-edit-buttons-bg-color: rgba(245,235,220,0.95);
    --mes-edit-buttons-border-width: 1px;
    --mes-edit-buttons-border-color: #8b735b;
    --mes-edit-buttons-border-radius: 5px;
    --mes-edit-buttons-box-shadow: 2px 2px 5px rgba(118,98,72,0.2);
    --mes-button-color: #4d3e2e;
    --mes-button-padding: 4px 8px;
}



/* 消息容器 */
body.bubblechat .mes {
    position: relative;
    background: 
        linear-gradient(var(--text-pole-bg-gradient-start), var(--text-pole-bg-gradient-end)),
        repeating-linear-gradient(
            -10deg,
            rgba(118,82,48,0.05) 0px,
            rgba(118,82,48,0.05) 2px,
            transparent 2px,
            transparent 5px
        ) !important;
    border-radius: 0 !important;
    transition: transform 0.3s ease;
}
/* 消息*/
.mes_text {
    position: relative;
    margin: var(--mes-text-margin) !important;
    padding: var(--mes-text-padding) !important;
    border: var(--mes-text-border-width) solid var(--mes-text-border-color) !important;
    border-radius: var(--mes-text-border-radius) !important;
    background: 
        linear-gradient(175deg, 
            rgba(245,235,220,0.98) 0%, 
            rgba(230,215,195,0.95) 100%) !important;
    box-shadow: 
        var(--mes-text-box-shadow-1),
        var(--mes-text-box-shadow-2);
    transition: box-shadow 0.3s ease;
}


/* 消息容器 */
.mes {
    position: relative;
    margin: 1.5em 0;
    padding: 15px;
    background: linear-gradient(145deg, var(--mes-bg-gradient-start) 0%, var(--mes-bg-gradient-end) 100%)!important;
    width: 100%;
    flex-direction: column;
    align-items: center;
    border: none !important;
    margin: 0;
}
.mes_block {
    margin: 0 !important;
    width: 98%;
    padding-left: 0;
}

.last_mes .mes_text {
  padding-right: 0;
}

.last_mes .mesAvatarWrapper {
  padding-bottom: 0;
}


body.bubblechat .mes {
    margin: -1px 0 !important;
    border-radius: 0 !important;
}

.avatar img {
    width: var(--avatar-size) !important;
    height: var(--avatar-size) !important;
    border: 3px solid var(--avatar-border-color) !important;
    border-radius: 50% !important;
}

/* 动态墨水斑点 */
@keyframes inkSpread {
    0% { transform: scale(0); opacity: 0.8; }
    100% { transform: scale(2); opacity: 0; }
}

body.bubblechat .mes:hover::after {
    content: '';
    position: absolute;
    width: 18px;
    height: 18px;
    background: var(--ink-spot-color);
    border-radius: 50%;
    animation: inkSpread 1.1s ease-out;
    top: 70%;
    left: 80%;
    pointer-events: none;
}

/* ======== 按钮系统 ======== */
#send_but {
    background: var(--send-but-bg-color) !important;
    border: none !important;
    border-radius: var(--send-but-border-radius);
    padding: var(--send-but-padding);
    box-shadow:
        2px 2px 0 var(--send-but-box-shadow-color),
        inset 1px 1px 2px var(--send-but-inset-shadow-color);
    transition: all 0.2s ease;
    font-family: 'ZhuqueFangsong';
    color: #E2D9CC!important;
    position: relative;
    overflow: hidden;
}
#send_textarea:placeholder-shown {
font-family: 'ZhuqueFangsong';
}
#send_but::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: rgba(255,255,255,0.2);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    transition: width 0.3s, height 0.3s;
}

#send_but:hover::after {
    width: 100px;
    height: 100px;
}

/* ======== 移动端适配 ======== */


/* 输入区背景板 */
.text_pole,
textarea {
    border-radius: 10px !important;
    background-color: var(--text-pole-bg-color) !important;
    background-image: linear-gradient(
        to bottom, 
        var(--text-pole-bg-gradient-start), 
        var(--text-pole-bg-gradient-end)
    ) !important;
    color: var(--text-pole-font-color) !important;
    border: var(--text-pole-border-width) solid var(--text-pole-border-color) !important;
    box-shadow: 
        var(--text-pole-box-shadow-1),
        var(--text-pole-box-shadow-2);
}

/* 预设关字体 */
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name,
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
    color: var(--prompt-disabled-font-color);
}

/* 删除按钮 */
#delete_button,
.redWarningBG,
.menu_button.popup-button-ok {
    background: linear-gradient(
        to bottom, 
        var(--delete-button-bg-gradient-start), 
        var(--delete-button-bg-gradient-end)
    ) !important;
    border: var(--delete-button-border-width) solid  !important;
    border-radius: var(--delete-button-border-radius);
    box-shadow: 
        var(--delete-button-box-shadow-1),
        var(--delete-button-box-shadow-2);
    color: #f5ebdc !important;
}

/* 时间戳 */
.mes_time {
    color: var(--mes-time-font-color) !important;
    text-shadow: 0.5px 0.5px var(--mes-time-text-shadow-color);
    font-size: 0.75em;
    letter-spacing: 0.5px; 
}

/* 设置标题底色去除 */
.title_restorable {
    background-image: none !important;
    background-color: var(--title-restorable-bg-color);
    box-shadow: var(--title-restorable-box-shadow);
}

/* 设置标题字体修改 */
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4,
.standoutHeader {
    background-image: none !important;
    background-color: var(--settings-title-bg-color);
    color: var(--settings-title-font-color);
    text-shadow: 1px 1px 2px var(--settings-title-text-shadow-color);
    border-left-width: var(--settings-title-border-left-width); 
    padding: var(--settings-title-padding); 
}

/* 编辑框黑色去除 */
.edit_textarea {
    background-color: var(--edit-textarea-bg-color) !important;
}

body.bubblechat .mes_block {
    padding-bottom: unset;
}

/* 隐藏代理警告 */
#ReverseProxyWarningMessage {
    display: none !important;
}

/* 标注区域 */
.mes_text blockquote {
    background-color: var(--blockquote-bg-color);
    border-left: 5px solid var(--blockquote-border-color);
    color: var(--blockquote-font-color);
}

body.waifuMode #chat {
    border-radius: 12px 12px 0 0;
    border-top: var(--waifu-chat-and-scrollbar);
    border-left: var(--waifu-chat-and-scrollbar);
    border-right: var(--waifu-chat-and-scrollbar);
    border-bottom: none;
}

body.waifuMode #form_sheld {
    border-top: none;
    padding-bottom: 1%;
}

body.waifuMode #top-bar {
    height: 0px;
    border: none;
    box-shadow: none; 
}

body.waifuMode #top-settings-holder {
    background-color: transparent;
    opacity: 0;
    max-height: 30px;
    pointer-events: all; 
    transition: opacity 1.0s ease, visibility 1.0s ease, max-height 1.0s ease, pointer-events 0s ease;
}

body.waifuMode #top-settings-holder:hover {
    opacity: 1;
    visibility: visible;
    max-height: 80px;
    pointer-events: auto; 
}

* {
    scrollbar-width: thin;
    scrollbar-color: rgba(118,98,72,0.4) rgba(245,235,220,0.3);
}

/* 时间戳样式 */
.mes_time {
    color: var(--mes-time-font-color) !important;
    text-shadow: 0.5px 0.5px var(--mes-time-text-shadow-color);
    font-size: 0.75em;
    letter-spacing: 0.5px; 
}

/* 标题系统 */
.title_restorable {
    background-color: var(--title-restorable-bg-color);
    box-shadow: var(--title-restorable-box-shadow);
}

#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4 {
    background-color: var(--settings-title-bg-color);
    color: var(--settings-title-font-color);
    border-left-width: var(--settings-title-border-left-width); 
    padding: var(--settings-title-padding); 
}

pre, code {
    background: 
        linear-gradient(160deg, var(--pre-bg-gradient-start) 0%, var(--pre-bg-gradient-end) 100%) !important;
    border: var(--pre-border-width) solid var(--pre-border-color) !important;
    border-radius: var(--pre-border-radius) !important;
    color: #e6d8c3 !important;
    font-family: 'Ma Shan Zheng', cursive;
    position: relative;
    box-shadow: 
        var(--pre-box-shadow-1),
        var(--pre-box-shadow-2);
}

pre::before {
    content: '';
    position: absolute;
    top: calc(-1 * var(--pre-border-width));
    left: calc(-1 * var(--pre-border-width));
    right: calc(-1 * var(--pre-border-width));
    bottom: calc(-1 * var(--pre-border-width));
    border: var(--pre-border-outside-width) solid var(--pre-border-outside-color);
    border-radius: var(--pre-border-outside-radius);
    pointer-events: none;
}

/* 移动端滚动条隐藏 */
@media screen and (max-width: 1000px) {
    *::-webkit-scrollbar {
        display: none;
    }

    * {
        scrollbar-width: none;
        -ms-overflow-style: none;
    }
}

#top-settings-holder {
    background: linear-gradient(var(--top-settings-holder-bg-gradient-start), var(--top-settings-holder-bg-gradient-end)) !important;
    box-shadow: var(--top-settings-holder-box-shadow);
}

.drawer-icon {
    color: var(--drawer-icon-color) !important;
    font-size: var(--drawer-icon-font-size);
}

.mes_edit_buttons {
    background: var(--mes-edit-buttons-bg-color) !important;
    border: var(--mes-edit-buttons-border-width) solid var(--mes-edit-buttons-border-color) !important;
    border-radius: var(--mes-edit-buttons-border-radius);
    box-shadow: var(--mes-edit-buttons-box-shadow);
}

.mes_button {
    color: var(--mes-button-color) !important;
    padding: var(--mes-button-padding);
    transition: all 0.2s ease;
}

.mes_button:hover {
    background: rgba(139,115,91,0.1) !important;
    transform: scale(1.05);
}

/* 角色切换器优化 */
#HotSwapWrapper > div {
    padding: 2px 0;
}

.character_select {
    transition: transform 0.3s ease;
}

.character_select:hover {
    transform: translateY(-3px);
}

